import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Header.css'; // Asegúrate de que la ruta sea correcta
import headerImage from '../images/header-background.jpg'; // Asegúrate de que la ruta sea correcta

const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);
    const navigate = useNavigate();

    // Función para cerrar el menú al hacer clic en un enlace
    const handleLinkClick = () => {
        setMenuOpen(false);
    };

    return (
        <header
            className="header-container"
            style={{ backgroundImage: `url(${headerImage})` }}
        >
            {/* Logo siempre a la izquierda */}
            <Link to="/" className="logo-container" onClick={handleLinkClick}>
                <img src="/images/logo.png" alt="Logo del Hostal" className="logo" />
                <span className="logo-text">Hostal Jallalla</span>
            </Link>

            {/* Contenedor de la navegación */}
            <nav className={`navbar ${menuOpen ? 'is-active' : ''}`}>
                <ul className="nav-links">
                    <li><Link to="/" className="nav-button" onClick={handleLinkClick}>Inicio</Link></li>
                    <li><Link to="/nosotros" className="nav-button" onClick={handleLinkClick}>Nosotros</Link></li>
                    <li><Link to="/galeria" className="nav-button" onClick={handleLinkClick}>Galería</Link></li>
                    <li>
                        <button className="admin-button" onClick={() => {
                            navigate('/admin');
                            handleLinkClick();
                        }}>
                            Administrador
                        </button>
                    </li>
                </ul>
            </nav>

            {/* Botón Hamburguesa (solo visible en móviles) */}
            <button
                className={`menu-toggle ${menuOpen ? 'is-active' : ''}`}
                onClick={() => setMenuOpen(!menuOpen)}
                aria-label="Toggle menu"
            >
                <span className="hamburger-line"></span>
                <span className="hamburger-line"></span>
                <span className="hamburger-line"></span>
            </button>
        </header>
    );
};

export default Header;
