import React from 'react';
import { Link } from 'react-router-dom';

const RoomCard = ({ room, availability, selectedOptions, handleSelectChange }) => {
    // Verificar disponibilidad de forma segura
    const isAvailable = availability[room.id] ?? true; // Por defecto, disponible si no se define

    return (
        <div className="room-card">
            {/* Imagen de la habitación */}
            <img src={room.image} alt={room.type} className="room-image" />

            {/* ID de la habitación */}
            <p className="room-id">
                ID: <strong>{room.id}</strong>
            </p>

            {/* Nombre de la habitación */}
            <h2>{room.type}</h2>

            {/* Precio dinámico */}
            <p>
                Precio:{" "}
                <strong>
                    {selectedOptions[room.id] === 1
                        ? `${room.priceForOne} CLP`
                        : `${room.priceForTwo || room.priceForOne} CLP`}
                </strong>
            </p>

            {/* Selector de personas */}
            {![4, 5, 8].includes(room.id) && (
                <label>
                    Personas:
                    <select
                        value={selectedOptions[room.id] || 1} // Valor por defecto
                        onChange={(e) => handleSelectChange(room.id, parseInt(e.target.value))}
                    >
                        <option value={1}>1 Persona</option>
                        {room.priceForTwo && <option value={2}>2 Personas</option>}
                    </select>
                </label>
            )}

            {/* Disponibilidad */}
            <p className="room-availability">
                <span className={isAvailable ? "available" : "not-available"}>
                    {isAvailable ? "Disponible" : "No disponible"}
                </span>
            </p>

            {/* Botón Ver Detalles */}
            <Link
                to={`/room/${room.id}`}
                className={`details-btn ${isAvailable ? "" : "disabled"}`}
                onClick={(e) => !isAvailable && e.preventDefault()}
                style={{
                    pointerEvents: isAvailable ? "auto" : "none",
                    backgroundColor: isAvailable ? "#007bff" : "#ccc",
                    color: isAvailable ? "white" : "#666",
                    cursor: isAvailable ? "pointer" : "not-allowed",
                }}
            >
                {isAvailable ? "Ver Detalles" : "No disponible"}
            </Link>
        </div>
    );
};

export default RoomCard;
