import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Importa los estilos por defecto
import '../styles/Calendar.css'; // Importa tus estilos personalizados


const DateSelector = ({ onDatesSelected }) => {
    const [dates, setDates] = useState([new Date(), new Date()]); // Fecha inicial

    const handleChange = (selectedDates) => {
        setDates(selectedDates);
        if (onDatesSelected) {
            onDatesSelected(selectedDates);
        }
    };

    return (
        <div>
            <h2>Selecciona las fechas</h2>
            <Calendar
                selectRange={true} // Permitir seleccionar rango
                onChange={handleChange}
                value={dates}
            />
        </div>
    );
};

export default DateSelector;
