import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const RoomDetails = () => {
    const { id } = useParams(); // Obtén el ID de la habitación desde la URL
    const navigate = useNavigate(); // Para navegar de regreso

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            <h1>Descripción de la Habitación {id}</h1>
            <p>Aquí puedes agregar más detalles sobre la habitación, como:</p>
            <ul>
                <li>Tipo de habitación</li>
                <li>Precio</li>
                <li>Comodidades</li>
                <li>Disponibilidad</li>
            </ul>
            <div style={{ marginTop: '20px' }}>
                {/* Botón Volver */}
                <button
                    onClick={() => navigate(-1)}
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#007bff',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        fontSize: '16px',
                        marginRight: '10px',
                    }}
                >
                    Volver
                </button>

                {/* Botón Reservar */}
                <button
                    onClick={() => alert('Función de reservar en desarrollo')} // Aquí se colocará la lógica para reservar
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#28a745',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        fontSize: '16px',
                    }}
                >
                    Reservar
                </button>
            </div>
        </div>
    );
};

export default RoomDetails;
