import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Header.css';
import headerImage from '../images/header-background.jpg'; // Importa la imagen

const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);
    const navigate = useNavigate();

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    return (
        <header
            className="header-container"
            style={{ backgroundImage: `url(${headerImage})` }} // Usa la imagen importada
        >
            <nav className="nav-container" style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                {/* Logo */}
                <div className="logo-container" style={{ display: "flex", alignItems: "center" }}>
                    <img src="/images/logo.png" alt="Logo del Hostal" className="logo" />
                    <span className="logo-text">Hostal Jallalla</span>
                </div>

                {/* Menú hamburguesa */}
                <div className="menu-icon" onClick={toggleMenu}>
                    &#9776;
                </div>

                {/* Botones de navegación */}
                <ul className={`nav-links ${menuOpen ? 'active' : ''}`} style={{ display: "flex", gap: "15px" }}>
                    <li><Link to="/" className="nav-button">Inicio</Link></li>
                    <li><Link to="/nosotros" className="nav-button">Nosotros</Link></li>
                    <li><Link to="/galeria" className="nav-button">Galería</Link></li>
                    <li>
                        <button className="admin-button" onClick={() => navigate('/admin')}>
                            Administrador
                        </button>
                    </li>
                </ul>

                {/* Botón decorativo en el lado derecho */}
                <button
                    className="decorative-button"
                    style={{
                        padding: "10px 20px",
                        fontSize: "14px",
                        fontWeight: "bold",
                        color: "white",
                        backgroundColor: "#28a745",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                    }}
                >
                    Decorativo
                </button>
            </nav>
        </header>
    );
};

export default Header;
