import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import RoomCard from './RoomCard';

const RoomList = ({ rooms, availability }) => {
    const [selectedOptions, setSelectedOptions] = useState(
        rooms.reduce((acc, room) => ({ ...acc, [room.id]: 1 }), {})
    );

    const handleSelectChange = (id, value) => {
        setSelectedOptions((prev) => ({ ...prev, [id]: value }));
    };

    console.log("Disponibilidad recibida en RoomList:", availability); // Depuración

    return (
        <div className="room-list">
            {rooms.map((room) => (
                <div key={room.id} className="room-card">
                    <img src={room.image} alt={room.type} className="room-image" />

                    <p className="room-id">ID: <strong>{room.id}</strong></p>
                    <h2>{room.type}</h2>
                    <p>
                        Precio:{" "}
                        <strong>
                            {selectedOptions[room.id] === 1
                                ? `${room.priceForOne} CLP`
                                : `${room.priceForTwo || room.priceForOne} CLP`}
                        </strong>
                    </p>

                    {![4, 5, 8].includes(room.id) && (
                        <label>
                            Personas:
                            <select
                                value={selectedOptions[room.id]}
                                onChange={(e) => handleSelectChange(room.id, parseInt(e.target.value))}
                            >
                                <option value={1}>1 Persona</option>
                                {room.priceForTwo && <option value={2}>2 Personas</option>}
                            </select>
                        </label>
                    )}

                    <p className="room-availability">
                        Disponibilidad:{" "}
                        <span className={availability[room.id] ? "available" : "not-available"}>
                            {availability[room.id] ? "Disponible" : "No disponible"}
                        </span>
                    </p>

                    <Link
                        to={`/room/${room.id}`}
                        className={`details-btn ${availability[room.id] ? "" : "disabled"}`}
                        onClick={(e) => !availability[room.id] && e.preventDefault()}
                    >
                        {availability[room.id] ? "Ver Detalles" : "No Disponible"}
                    </Link> <div className="room-list"> {/* Asegúrate de que esta clase existe */}
            {rooms.map((room) => (
                <RoomCard
                    key={room.id}
                    room={room}
                    availability={availability[room.id]}
                    selectedOptions={selectedOptions}
                    handleSelectChange={handleSelectChange}
                />
            ))}
        </div>
                    
                </div>
            ))}
        </div>
    );
};

export default RoomList;
