import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import RoomList from '../components/RoomList';
import roomsData from '../data/roomsData';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';
import '../styles/Home.css';

registerLocale("es", es);

const Home = () => {
    const navigate = useNavigate();

    // Fechas iniciales
    const today = new Date();
    const tomorrow = new Date(today.getTime() + 24 * 60 * 60 * 1000);

    const [checkIn, setCheckIn] = useState(today);
    const [checkOut, setCheckOut] = useState(tomorrow);
    const [reservations, setReservations] = useState([]);
    const [availability, setAvailability] = useState({});

    const sortedRoomsData = [...roomsData].sort((a, b) => a.id - b.id);

    // Obtener reservas
    const fetchReservations = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/reservations");
            const data = await response.json();
            setReservations(data);
        } catch (error) {
            console.error("Error al obtener reservas:", error);
        }
    };

    // Calcular disponibilidad
    const calculateAvailability = () => {
        const updatedAvailability = {};
        sortedRoomsData.forEach((room) => {
            let isAvailable = true;
            reservations.forEach((res) => {
                const resCheckIn = new Date(res.checkIn).getTime();
                const resCheckOut = new Date(res.checkOut).getTime();
                const selectedCheckIn = checkIn.getTime();
                const selectedCheckOut = checkOut.getTime();

                // Comparar si hay solapamiento
                if (
                    room.id === res.roomId &&
                    selectedCheckOut > resCheckIn &&
                    selectedCheckIn < resCheckOut
                ) {
                    isAvailable = false;
                }
            });
            updatedAvailability[room.id] = isAvailable;
        });
        setAvailability(updatedAvailability);
    };

    useEffect(() => {
        fetchReservations();
    }, []);

    useEffect(() => {
        if (reservations.length > 0) {
            calculateAvailability();
        }
    }, [checkIn, checkOut, reservations]);

    return (
        <div className="home-container">
            <h1 className="home-title">Bienvenido al Hostal</h1>

            {/* Calendarios */}
            <div className="calendar-container">
                <label>Fecha de ingreso:</label>
                

                <label>Fecha de salida:</label>
                <DatePicker
                    selected={checkOut}
                    onChange={(date) => date > checkIn && setCheckOut(date)}
                    locale="es"
                    minDate={new Date(checkIn.getTime() + 24 * 60 * 60 * 1000)}
                    dateFormat="dd/MM/yyyy"
                    className="datepicker-input"
                />
            </div>

            {/* Bot√≥n Administrador */}
            <div className="admin-button-container">
                <button onClick={() => navigate('/admin')} className="admin-button">
                    Administrador
                </button>
            </div>

            {/* Lista de habitaciones */}
            <RoomList rooms={sortedRoomsData} availability={availability} />
        </div>
    );
};

export default Home;
