import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import RoomList from '../components/RoomList';
import roomsData from '../data/roomsData';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';
import '../styles/Home.css';
import Footer from '../components/Footer';

registerLocale("es", es);

const Home = () => {
    const navigate = useNavigate();

    // Fechas iniciales
    const today = new Date();
    const tomorrow = new Date(today.getTime() + 24 * 60 * 60 * 1000);

    const [checkIn, setCheckIn] = useState(today);
    const [checkOut, setCheckOut] = useState(tomorrow);
    const [availability, setAvailability] = useState({});
    const [selectedOptions, setSelectedOptions] = useState(
        roomsData.reduce((acc, room) => ({ ...acc, [room.id]: 1 }), {})
    );

    const sortedRoomsData = [...roomsData].sort((a, b) => a.id - b.id);

    const calculateAvailability = async () => {
        const updatedAvailability = {};

        for (const room of sortedRoomsData) {
            try {
                const response = await fetch("https://hostaljallalla2.onrender.com/api/reservations/check-availability", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({
                        roomId: room.id,
                        checkIn: checkIn.toISOString().split("T")[0],
                        checkOut: checkOut.toISOString().split("T")[0],
                    }),
                });

                const data = await response.json();
                updatedAvailability[room.id] = !data.unavailableRooms.includes(room.id);
            } catch (error) {
                console.error(`Error al verificar disponibilidad para la habitación ${room.id}:`, error);
                updatedAvailability[room.id] = false; // Marcar como no disponible si hay un error
            }
        }

        setAvailability(updatedAvailability);
    };

    useEffect(() => {
        calculateAvailability();
    }, [checkIn, checkOut]);

    const handleSelectChange = (id, value) => {
        setSelectedOptions((prev) => ({ ...prev, [id]: value }));
    };

    return (
        <div className="home-container">
            <h1 className="home-title">Bienvenido al Hostal Jallalla</h1>

            {/* Calendarios */}
            <div className="calendar-container">
                <label>Elige una fecha</label>
                <DatePicker
                    selected={checkIn}
                    onChange={(date) => setCheckIn(date)}
                    locale="es"
                    minDate={today}
                    dateFormat="dd/MM/yyyy"
                />

                <DatePicker
                    selected={checkOut}
                    onChange={(date) => date > checkIn && setCheckOut(date)}
                    locale="es"
                    minDate={new Date(checkIn.getTime() + 24 * 60 * 60 * 1000)}
                    dateFormat="dd/MM/yyyy"
                    className="hidden-calendar"
                />
            </div>

            {/* Lista de habitaciones */}
            <RoomList
                rooms={sortedRoomsData}
                availability={availability}
                selectedOptions={selectedOptions}
                handleSelectChange={handleSelectChange}
            />
            <Footer />
        </div>
    );
};

export default Home;
