import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RoomList = ({ rooms, dates }) => {
    const [unavailableRooms, setUnavailableRooms] = useState([]);

    // Verificar disponibilidad cuando cambien las fechas
    useEffect(() => {
        const checkAvailability = async () => {
            const [checkIn, checkOut] = dates;
            try {
                const response = await axios.post(`${process.env.REACT_APP_API_URL}/reservations/check-availability`, {
                    checkIn: checkIn.toISOString(),
                    checkOut: checkOut.toISOString(),
                });
                setUnavailableRooms(response.data.unavailableRooms);
            } catch (error) {
                console.error('Error al verificar disponibilidad:', error);
            }
        };

        if (dates) checkAvailability();
    }, [dates]);

    return (
        <div className="rooms-list">
            {rooms.map((room) => (
                <div key={room.id} className="room-card">
                    <h3>{room.type}</h3>
                    <p>ID: {room.id}</p>
                    <p>Precio para 1 persona: {room.priceForOne} CLP</p>
                    {room.priceForTwo && <p>Precio para 2 personas: {room.priceForTwo} CLP</p>}
                    <p>
                        Disponibilidad:{' '}
                        {unavailableRooms.includes(room.id) ? (
                            <span style={{ color: 'red' }}>No disponible</span>
                        ) : (
                            <span style={{ color: 'green' }}>Disponible</span>
                        )}
                    </p>
                    <button
                        disabled={unavailableRooms.includes(room.id)}
                        style={{
                            backgroundColor: unavailableRooms.includes(room.id) ? 'red' : 'green',
                            color: 'white',
                        }}
                    >
                        {unavailableRooms.includes(room.id) ? 'No disponible' : 'Reservar'}
                    </button>
                </div>
            ))}
        </div>
    );
};

export default RoomList;
