import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';

registerLocale("es", es);

const RoomList = ({ rooms }) => {
    const [selectedDate, setSelectedDate] = useState(null); // Fecha seleccionada
    const [availability, setAvailability] = useState({}); // Estado de disponibilidad

    // Función para verificar disponibilidad desde el backend
    const fetchAvailability = async (date) => {
        if (!date) return; // No hacer nada si no hay fecha
        const formattedDate = date.toISOString().split("T")[0];

        try {
            const response = await fetch("http://localhost:5000/api/reservations/check-availability", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    checkIn: formattedDate,
                    checkOut: formattedDate,
                }),
            });

            const data = await response.json();
            console.log("Datos recibidos del backend:", data);

            const updatedAvailability = rooms.reduce((acc, room) => {
                acc[room.id] = !data.unavailableRooms?.includes(room.id);
                return acc;
            }, {});

            setAvailability(updatedAvailability);
        } catch (error) {
            console.error("Error al verificar disponibilidad:", error);
        }
    };

    // Actualizar disponibilidad cuando cambia la fecha seleccionada
    useEffect(() => {
        if (selectedDate) {
            fetchAvailability(selectedDate);
        }
    }, [selectedDate]);

    return (
        <div className="room-list-container">
            {/* Calendario */}
            <div className="calendar-container">
                <p>Selecciona una fecha para ver disponibilidad:</p>
                <DatePicker
                    selected={selectedDate}
                    onChange={(date) => setSelectedDate(date)}
                    locale="es"
                    placeholderText="Selecciona una fecha"
                    inline
                />
            </div>

            {/* Lista de habitaciones */}
            <div className="room-list">
                {rooms.map((room) => (
                    <div key={room.id} className="room-card">
                        <img src={room.image} alt={room.type} className="room-image" />
                        <p className="room-id">ID: <strong>{room.id}</strong></p>
                        <h2>{room.type}</h2>
                        <p>Precio: <strong>{room.priceForOne} CLP</strong></p>
                        <p className="room-availability">
                            Disponibilidad:{" "}
                            <span className={availability[room.id] ? "available" : "not-available"}>
                                {availability[room.id] ? "Disponible" : "No disponible"}
                            </span>
                        </p>
                        <Link
                            to={`/room/${room.id}`}
                            className={`details-btn ${availability[room.id] ? "" : "disabled"}`}
                        >
                            {availability[room.id] ? "Ver Detalles" : "No Disponible"}
                        </Link>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default RoomList;
