import React, { useState } from 'react';
import roomsData from '../data/roomsData';
import RoomList from '../components/RoomList';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'; // Estilos del calendario
import '../styles/Home.css';
import es from "date-fns/locale/es"; // Importa el idioma español

registerLocale("es", es); // Registra el idioma español

const Home = () => {
    const [rooms, setRooms] = useState(roomsData); // Estado de habitaciones
    const [selectedDate, setSelectedDate] = useState(null); // Estado para la fecha seleccionada

    // Manejar cambio de fecha
    const handleDateChange = (date) => {
        setSelectedDate(date);
        console.log("Fecha seleccionada:", date);
        // Aquí puedes realizar una llamada al backend para obtener disponibilidad
    };

    return (
        <div className="home-container">
            <h1>Bienvenido al Hostal</h1>

            {/* Contenedor del calendario */}
            <div className="calendar-container">
                <p>Selecciona una fecha para ver disponibilidad:</p>
                <DatePicker
                    selected={selectedDate}
                    onChange={handleDateChange}
                    locale="es" // Establecer idioma español
                    placeholderText="Selecciona una fecha"
                    inline
                />
            </div>

            {/* Lista de habitaciones */}
            <RoomList rooms={rooms} />
        </div>
    );
};

export default Home;
