import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import RoomList from '../components/RoomList';
import roomsData from '../data/roomsData';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';
import '../styles/Home.css';

registerLocale("es", es);

const Home = () => {
    const navigate = useNavigate();

    // Fechas iniciales
    const today = new Date();
    const tomorrow = new Date(today.getTime() + 24 * 60 * 60 * 1000);

    const [checkIn, setCheckIn] = useState(today);
    const [checkOut, setCheckOut] = useState(tomorrow);
    const [reservations, setReservations] = useState([]); // Lista de reservas desde la base de datos
    const [availability, setAvailability] = useState({}); // Estado de disponibilidad

    const sortedRoomsData = [...roomsData].sort((a, b) => a.id - b.id);

    // Obtener reservas desde el backend
    const fetchReservations = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/reservations"); // Ajusta el endpoint
            const data = await response.json();
            setReservations(data); // Guardar todas las reservas
        } catch (error) {
            console.error("Error al obtener las reservas:", error);
        }
    };

    // Comparar fechas para determinar disponibilidad
    const calculateAvailability = () => {
        const updatedAvailability = {};

        sortedRoomsData.forEach((room) => {
            const isRoomAvailable = !reservations.some((res) => {
                // Convertir las fechas a objetos Date
                const resCheckIn = new Date(res.checkIn);
                const resCheckOut = new Date(res.checkOut);

                // Comparar las fechas seleccionadas con las reservas
                return (
                    room.id === res.roomId && // Comparar por ID de habitaci贸n
                    !(checkOut <= resCheckIn || checkIn >= resCheckOut) // Solapamiento
                );
            });

            updatedAvailability[room.id] = isRoomAvailable;
        });

        setAvailability(updatedAvailability);
    };

    // L贸gica para manejar cambio de fechas
    const handleCheckInChange = (date) => {
        setCheckIn(date);
        const nextDay = new Date(date.getTime() + 24 * 60 * 60 * 1000);
        setCheckOut(nextDay);
    };

    // Ejecutar al cargar el componente y cuando cambien las fechas
    useEffect(() => {
        fetchReservations();
    }, []);

    useEffect(() => {
        if (reservations.length > 0) {
            calculateAvailability();
        }
    }, [checkIn, checkOut, reservations]);

    return (
        <div className="home-container">
            <h1 className="home-title">Bienvenido al Hostal</h1>

            <div className="calendar-container">
                <label>Fecha de ingreso:</label>
                <DatePicker
                    selected={checkIn}
                    onChange={handleCheckInChange}
                    locale="es"
                    minDate={today}
                    dateFormat="dd/MM/yyyy"
                />

                <label>Fecha de salida:</label>
                <DatePicker
                    selected={checkOut}
                    onChange={(date) => date > checkIn && setCheckOut(date)}
                    locale="es"
                    minDate={new Date(checkIn.getTime() + 24 * 60 * 60 * 1000)}
                    dateFormat="dd/MM/yyyy"
                />
            </div>

            <div className="admin-button-container">
                <button onClick={() => navigate('/admin')} className="admin-button">
                    Administrador
                </button>
            </div>

            <RoomList rooms={sortedRoomsData} availability={availability} />
        </div>
    );
};

export default Home;
