import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import jsPDF from 'jspdf';

const ReservationSuccess = () => {
    const location = useLocation(); // Obtén los detalles de la reserva desde el estado
    const navigate = useNavigate();
    const reservation = location.state?.reservation; // Asegúrate de que los datos están disponibles

    if (!reservation) {
        return <p>No se encontraron detalles de la reserva.</p>;
    }

    const handleDownloadPDF = () => {
        const doc = new jsPDF();

        // Título
        doc.setFontSize(18);
        doc.text('Detalles de tu Reserva', 10, 20);

        // Contenido
        doc.setFontSize(12);
        doc.text(`ID de la Habitación: ${reservation.roomId}`, 10, 40);
        doc.text(`Nombre del Cliente: ${reservation.clientName}`, 10, 50);
        doc.text(`Cantidad de Personas: ${reservation.numberOfGuests}`, 10, 60);
        doc.text(`Fecha de Ingreso: ${reservation.checkIn}`, 10, 70);
        doc.text(`Fecha de Salida: ${reservation.checkOut}`, 10, 80);
        doc.text(`Cantidad de Noches: ${reservation.nights}`, 10, 90);
        doc.text(`Costo Total: ${reservation.totalCost} CLP`, 10, 100);

        // Descargar el PDF
        doc.save('Reserva.pdf');
    };

    return (
        <div style={{ textAlign: 'center', padding: '50px' }}>
            <h1 style={{ color: 'green' }}>¡Felicidades!</h1>
            <p>Tu reserva ha sido realizada exitosamente.</p>
            <img 
                src="/images/success.jpg" 
                alt="Reserva Exitosa" 
                style={{ width: '200px', margin: '20px 0' }} 
            />
            <p>
                <strong>Detalles de la Reserva:</strong>
            </p>
            <ul style={{ listStyle: 'none', textAlign: 'left', display: 'inline-block' }}>
                <li>ID de la Habitación: {reservation.roomId}</li>
                <li>Nombre del Cliente: {reservation.clientName}</li>
                <li>Cantidad de Personas: {reservation.numberOfGuests}</li>
                <li>Fecha de Ingreso: {reservation.checkIn}</li>
                <li>Fecha de Salida: {reservation.checkOut}</li>
                <li>Cantidad de Noches: {reservation.nights}</li>
                <li>Costo Total: {reservation.totalCost} CLP</li>
            </ul>
            <button
                onClick={() => navigate('/')}
                style={{
                    marginRight: '10px',
                    padding: '10px 20px',
                    backgroundColor: '#007bff',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                }}
            >
                Volver
            </button>
            <button
                onClick={handleDownloadPDF}
                style={{
                    padding: '10px 20px',
                    backgroundColor: '#28a745',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                }}
            >
                Descargar PDF
            </button>
        </div>
    );
};

export default ReservationSuccess;
