import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import rooms from '../data/rooms';

const RoomDetails = () => {
    const { id } = useParams(); // Obtén el ID de la habitación desde la URL
    const navigate = useNavigate(); // Para navegar de regreso
    const room = rooms.find((room) => room.id === parseInt(id)); // Busca la habitación por ID

    // Estado para el selector de personas (1 o 2 personas)
    const [selectedOption, setSelectedOption] = useState(1);

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            {/* Imagen de la habitación */}
            <img
                src={room.image}
                alt={room.type}
                style={{
                    width: '100%',
                    maxWidth: '600px',
                    borderRadius: '8px',
                    marginBottom: '20px',
                }}
            />

            {/* Información de la habitación */}
            <h1>{room.type}</h1>
            <p>
                Precio:{" "}
                <strong>
                    {selectedOption === 1
                        ? `${room.priceForOne} CLP`
                        : `${room.priceForTwo || room.priceForOne} CLP`}
                </strong>
            </p>
            <label>
                Personas:{" "}
                <select
                    value={selectedOption}
                    onChange={(e) => setSelectedOption(parseInt(e.target.value))}
                    style={{
                        marginLeft: '10px',
                        padding: '5px',
                        fontSize: '14px',
                        borderRadius: '4px',
                        border: '1px solid #ddd',
                    }}
                >
                    <option value={1}>1 Persona</option>
                    {room.priceForTwo && <option value={2}>2 Personas</option>}
                </select>
            </label>

            {/* Detalles adicionales */}
            <ul style={{ textAlign: 'left', margin: '20px auto', maxWidth: '600px' }}>
                <li>
                    <strong>Disponibilidad:</strong>{" "}
                    <span
                        style={{
                            color: room.available ? "#28a745" : "#dc3545",
                            fontWeight: "bold",
                        }}
                    >
                        {room.available ? "Disponible" : "No disponible"}
                    </span>
                </li>
                <li><strong>Comodidades:</strong> Ejemplo: Wifi, Baño Privado, Aire Acondicionado</li>
            </ul>

            {/* Botones de acción */}
            <div style={{ marginTop: '20px' }}>
                {/* Botón Volver */}
                <button
                    onClick={() => navigate(-1)}
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#007bff',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        fontSize: '16px',
                        marginRight: '10px',
                    }}
                >
                    Volver
                </button>

                {/* Botón Reservar */}
                <button
                    onClick={() => alert('Función de reservar en desarrollo')}
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#28a745',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        fontSize: '16px',
                    }}
                >
                    Reservar
                </button>
            </div>
        </div>
    );
};

export default RoomDetails;
