import React from 'react';

const RoomList = ({ rooms, availability }) => {
    return (
        <div className="rooms-list">
            {rooms.map((room) => (
                <div key={room.id} className="room-card">
                    <h3>{room.type}</h3>
                    <p>ID: {room.id}</p>
                    <p>Precio para 1 persona: {room.priceForOne} CLP</p>
                    {room.priceForTwo && <p>Precio para 2 personas: {room.priceForTwo} CLP</p>}
                    <p>
                        Disponibilidad:{' '}
                        {availability[room.id] === false ? (
                            <span style={{ color: 'red' }}>No disponible</span>
                        ) : (
                            <span style={{ color: 'green' }}>Disponible</span>
                        )}
                    </p>
                    <button
                        disabled={availability[room.id] === false}
                        style={{
                            backgroundColor: availability[room.id] === false ? 'red' : 'green',
                            color: 'white',
                        }}
                    >
                        {availability[room.id] === false ? 'No disponible' : 'Reservar'}
                    </button>
                </div>
            ))}
        </div>
    );
};

export default RoomList;
