import React from 'react';

const Nosotros = () => {
    return (
        <div
            style={{
                padding: '20px',
                textAlign: 'center',
                backgroundColor: '#f9f9f9',
                minHeight: '100vh',
            }}
        >
            <h1
                style={{
                    fontSize: '2.5rem',
                    color: '#007bff',
                    marginBottom: '20px',
                    fontWeight: 'bold',
                }}
            >
                Sobre Nosotros
            </h1>

            <p
                style={{
                    fontSize: '1.2rem',
                    lineHeight: '1.6',
                    color: '#333',
                    maxWidth: '800px',
                    margin: '0 auto 20px auto',
                }}
            >
                Bienvenido al <strong>Hostal Ejemplo</strong>, un lugar acogedor y moderno diseñado
                para que te sientas como en casa. Ofrecemos habitaciones cómodas y bien equipadas,
                ideales para turistas, familias y viajeros de negocios. Nuestro compromiso es
                brindarte una experiencia única, con atención personalizada y un ambiente
                agradable.
            </p>

            {/* Imagen del Hostal */}
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'center',
                    marginBottom: '20px',
                }}
            >
                <img
                    src="https://via.placeholder.com/600x400"
                    alt="Imagen del Hostal"
                    style={{
                        width: '100%',
                        maxWidth: '600px',
                        borderRadius: '10px',
                        boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
                    }}
                />
            </div>

            {/* Segunda sección de contenido */}
            <p
                style={{
                    fontSize: '1.1rem',
                    color: '#555',
                    maxWidth: '800px',
                    margin: '0 auto',
                }}
            >
                Ubicado en el corazón de la ciudad, nuestro hostal cuenta con fácil acceso a
                restaurantes, centros turísticos y transporte público. Contamos con habitaciones
                individuales, dobles y familiares, todas con precios accesibles y servicios de alta
                calidad.
            </p>
        </div>
    );
};

export default Nosotros;
