import React, { useState, useRef, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Header.css';
import headerImage from '../images/header-background.jpg';

const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);
    const navigate = useNavigate();
    const headerRef = useRef(null);

    // Función para cerrar el menú
    const closeMenu = () => setMenuOpen(false);

    // Cierra el menú si se hace clic fuera del header
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (headerRef.current && !headerRef.current.contains(event.target)) {
                closeMenu();
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    const handleAdminClick = () => {
        navigate('/admin');
        closeMenu();
    };

    return (
        <header className="header-container" ref={headerRef}>
            <div className="header-background" style={{ backgroundImage: `url(${headerImage})` }}></div>
            <div className="header-content">

                {/* Logo a la izquierda */}
                <Link to="/" className="logo-container" onClick={closeMenu}>
                    <img src="/images/logo.png" alt="Logo del Hostal" className="logo" />
                    <span className="logo-text">Hostal Jallalla</span>
                </Link>

                {/* Botón Hamburguesa (solo visible en móvil) */}
                <button
                    className={`menu-toggle ${menuOpen ? 'is-active' : ''}`}
                    onClick={() => setMenuOpen(!menuOpen)}
                    aria-label="Toggle menu"
                >
                    <span className="hamburger-line"></span>
                    <span className="hamburger-line"></span>
                    <span className="hamburger-line"></span>
                </button>

                {/* Navegación Principal */}
                <nav className={`main-nav ${menuOpen ? 'is-active' : ''}`}>
                    <ul className="nav-links">
                        <li><Link to="/" className="nav-button" onClick={closeMenu}>Inicio</Link></li>
                        <li><Link to="/nosotros" className="nav-button" onClick={closeMenu}>Nosotros</Link></li>
                        <li><Link to="/galeria" className="nav-button" onClick={closeMenu}>Galería</Link></li>
                        <li><button className="admin-button" onClick={handleAdminClick}>Administrador</button></li>
                    </ul>
                </nav>

            </div>
        </header>
    );
};

export default Header;
