import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import "jspdf-autotable";
import "./AdminPanel.css";
// Asegúrate de que la ruta a tus datos de habitaciones sea correcta
import roomsData from '../data/roomsData'; 

const AdminPanel = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    // --- Estados para los nuevos formularios ---
    const [manualData, setManualData] = useState({
        roomId: roomsData[0]?.id || '',
        clientName: '',
        rutOrPassport: '',
        phone: '',
        checkIn: new Date().toISOString().slice(0, 10),
        checkOut: new Date(new Date().setDate(new Date().getDate() + 1)).toISOString().slice(0, 10),
    });

    const [maintenanceData, setMaintenanceData] = useState({
        roomId: roomsData[0]?.id || '',
        checkIn: new Date().toISOString().slice(0, 10),
        checkOut: new Date(new Date().setDate(new Date().getDate() + 1)).toISOString().slice(0, 10),
    });

    // --- Funciones existentes (Login, Logout, etc.) ---
    const roomPrices = {
        1: { priceForOne: 17000, priceForTwo: 25000 },
        2: { priceForOne: 20000, priceForTwo: 30000 },
        3: { priceForOne: 25000, priceForTwo: 40000 },
    };

    const handleLogin = (e) => {
        e.preventDefault();
        if (username === "admin" && password === "jallalla1478") {
            setIsLoggedIn(true);
        } else {
            alert("Usuario o contraseña incorrectos.");
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        setPassword('');
        setReservations([]);
    };

    const fetchReservations = async () => {
        setLoading(true);
        try {
            const response = await axios.get("https://three-2cs3.onrender.com/api/reservations");
            // Aquí puedes seguir procesando las reservaciones como lo hacías antes
            setReservations(response.data);
        } catch (error) {
            console.error("Error al obtener reservas:", error);
        } finally {
            setLoading(false);
        }
    };

    const deleteReservation = async (id) => {
        if (!window.confirm("¿Estás seguro de eliminar esta reserva?")) return;
        try {
            await axios.delete(`https://three-2cs3.onrender.com/api/reservations/${id}`);
            fetchReservations();
        } catch (error) {
            console.error("Error al eliminar reserva:", error);
        }
    };

    // --- Nuevas funciones para los formularios ---
    const handleManualFormChange = (e) => {
        const { name, value } = e.target;
        setManualData(prev => ({ ...prev, [name]: value }));
    };

    const handleMaintenanceFormChange = (e) => {
        const { name, value } = e.target;
        setMaintenanceData(prev => ({ ...prev, [name]: value }));
    };

    const handleManualSubmit = async (e) => {
        e.preventDefault();
        const reservationData = {
            ...manualData,
            roomId: parseInt(manualData.roomId),
            numberOfGuests: 1, // Asumimos 1 huésped, puedes cambiarlo
        };

        try {
            await axios.post('https://three-2cs3.onrender.com/api/reservations', reservationData);
            alert('¡Reserva manual creada con éxito!');
            fetchReservations(); // Actualiza la lista
        } catch (error) {
            alert('Error al crear la reserva.');
        }
    };

    const handleMaintenanceSubmit = async (e) => {
        e.preventDefault();
        const maintenanceBooking = {
            ...maintenanceData,
            roomId: parseInt(maintenanceData.roomId),
            clientName: 'MANTENIMIENTO',
            rutOrPassport: 'N/A',
            phone: 'N/A',
            numberOfGuests: 0,
        };

        try {
            await axios.post('https://three-2cs3.onrender.com/api/reservations', maintenanceBooking);
            alert('¡Fechas bloqueadas por mantenimiento!');
            fetchReservations(); // Actualiza la lista para ver el bloqueo
        } catch (error) {
            alert('Error al bloquear fechas.');
        }
    };

    useEffect(() => {
        if (isLoggedIn) fetchReservations();
    }, [isLoggedIn]);


    if (!isLoggedIn) {
        return (
            <div className="login-container">
                <form onSubmit={handleLogin} className="login-form">
                    <h2>Iniciar Sesión</h2>
                    <input type="text" placeholder="Usuario" value={username} onChange={(e) => setUsername(e.target.value)} required />
                    <input type="password" placeholder="Contraseña" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    <button type="submit">Ingresar</button>
                </form>
            </div>
        );
    }

    return (
        <div className="admin-panel">
            <h2>Panel de Administración</h2>
            <div className="admin-actions">
                <button onClick={handleLogout} className="back-btn">Cerrar Sesión</button>
                <button onClick={() => navigate("/")} className="back-btn">Volver a Inicio</button>
                <button onClick={() => alert("Función de descarga PDF no implementada en este ejemplo")} className="back-btn">Descargar PDF</button>
            </div>

            {/* --- FORMULARIO PARA RESERVA MANUAL --- */}
            <div className="admin-section">
                <h3>Crear Reserva Manualmente</h3>
                <form onSubmit={handleManualSubmit} className="admin-form">
                    <select name="roomId" value={manualData.roomId} onChange={handleManualFormChange}>
                        {roomsData.map(room => <option key={room.id} value={room.id}>{room.type}</option>)}
                    </select>
                    <input name="clientName" type="text" placeholder="Nombre Cliente" value={manualData.clientName} onChange={handleManualFormChange} required />
                    <input name="rutOrPassport" type="text" placeholder="RUT o Pasaporte" value={manualData.rutOrPassport} onChange={handleManualFormChange} required />
                    <input name="phone" type="text" placeholder="Teléfono" value={manualData.phone} onChange={handleManualFormChange} />
                    <label>Check-in: <input name="checkIn" type="date" value={manualData.checkIn} onChange={handleManualFormChange} /></label>
                    <label>Check-out: <input name="checkOut" type="date" value={manualData.checkOut} onChange={handleManualFormChange} /></label>
                    <button type="submit">Crear Reserva</button>
                </form>
            </div>

            {/* --- FORMULARIO PARA MANTENIMIENTO --- */}
            <div className="admin-section">
                <h3>Bloquear Fechas por Mantenimiento</h3>
                <form onSubmit={handleMaintenanceSubmit} className="admin-form">
                    <select name="roomId" value={maintenanceData.roomId} onChange={handleMaintenanceFormChange}>
                        {roomsData.map(room => <option key={room.id} value={room.id}>{room.type}</option>)}
                    </select>
                    <label>Desde: <input name="checkIn" type="date" value={maintenanceData.checkIn} onChange={handleMaintenanceFormChange} /></label>
                    <label>Hasta: <input name="checkOut" type="date" value={maintenanceData.checkOut} onChange={handleMaintenanceFormChange} /></label>
                    <button type="submit">Bloquear Habitación</button>
                </form>
            </div>

            {/* --- TABLA DE RESERVAS EXISTENTE --- */}
            <div className="reservations-table">
                <h3>Listado de Reservas</h3>
                {loading ? <p>Cargando...</p> : (
                    <table>
                        <thead>
                            <tr>
                                <th>Cliente</th>
                                <th>Habitación</th>
                                <th>Check-In</th>
                                <th>Check-Out</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {reservations.map((res) => (
                                <tr key={res._id} className={res.clientName === 'MANTENIMIENTO' ? 'maintenance-row' : ''}>
                                    <td>{res.clientName}</td>
                                    <td>{roomsData.find(r => r.id === res.roomId)?.type || res.roomId}</td>
                                    <td>{new Date(res.checkIn).toLocaleDateString()}</td>
                                    <td>{new Date(res.checkOut).toLocaleDateString()}</td>
                                    <td>
                                        <button className="delete-btn" onClick={() => deleteReservation(res._id)}>Eliminar</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default AdminPanel;
