import React, { useContext, useEffect } from 'react';
import roomsData from '../data/roomsData';
import RoomList from '../components/RoomList';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import '../styles/Home.css';
import es from "date-fns/locale/es";
import { ReservationContext } from '../ReservationContext'; // Contexto global

registerLocale("es", es);

const Home = () => {
    const { selectedDate, setSelectedDate, availability, setAvailability } = useContext(ReservationContext);

    // Función para consultar la disponibilidad en base de datos
    const fetchAvailability = async (date) => {
        const formattedDate = date?.toISOString().split("T")[0]; // Formato YYYY-MM-DD
        try {
            const response = await fetch("http://localhost:5000/api/reservations/check-availability", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    checkIn: formattedDate,
                    checkOut: formattedDate, // Fecha de salida igual para un día
                }),
            });

            const data = await response.json();
            console.log("Habitaciones no disponibles:", data.unavailableRooms);

            // Actualizar disponibilidad: false si no disponible, true si disponible
            const updatedAvailability = roomsData.reduce((acc, room) => {
                acc[room.id] = !data.unavailableRooms?.includes(room.id); 
                return acc;
            }, {});

            setAvailability(updatedAvailability);
        } catch (error) {
            console.error("Error al verificar disponibilidad:", error);
        }
    };

    // Actualiza disponibilidad al cambiar la fecha seleccionada
    useEffect(() => {
        if (selectedDate) {
            fetchAvailability(selectedDate);
        }
    }, [selectedDate]);

    return (
        <div className="home-container">
            <h1>Bienvenido al Hostal</h1>

            {/* Calendario para seleccionar la fecha */}
            <div className="calendar-container">
                <p>Selecciona una fecha para ver disponibilidad:</p>
                <DatePicker
                    selected={selectedDate}
                    onChange={(date) => setSelectedDate(date)}
                    locale="es"
                    placeholderText="Selecciona una fecha"
                    inline
                />
            </div>

            {/* Lista de habitaciones */}
            <RoomList rooms={roomsData} availability={availability} />
        </div>
    );
};

export default Home;
