import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./AdminPanel.css";

const AdminPanel = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState(''); // Nuevo campo
    const [password, setPassword] = useState(''); // Contraseña
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleLogin = (e) => {
        e.preventDefault();
        if (username === "admin" && password === "admin123") {
            setIsLoggedIn(true);
        } else {
            alert("Usuario o contraseña incorrectos.");
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        setPassword('');
        setReservations([]);
    };

    const fetchReservations = async () => {
        setLoading(true);
        try {
            const response = await axios.get("http://localhost:5000/api/reservations");
            setReservations(response.data);
        } catch (error) {
            console.error("Error al obtener reservas:", error);
        } finally {
            setLoading(false);
        }
    };

    const deleteReservation = async (id) => {
        if (!window.confirm("¿Estás seguro de eliminar esta reserva?")) return;
        try {
            await axios.delete(`http://localhost:5000/api/reservations/${id}`);
            fetchReservations();
        } catch (error) {
            console.error("Error al eliminar reserva:", error);
        }
    };

    useEffect(() => {
        if (isLoggedIn) fetchReservations();
    }, [isLoggedIn]);

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            {!isLoggedIn ? (
                <form onSubmit={handleLogin}>
                    <h2>Iniciar Sesión</h2>
                    <input
                        type="text"
                        placeholder="Usuario"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        style={{ padding: "10px", marginBottom: "10px", width: "200px" }}
                        required
                    />
                    <br />
                    <input
                        type="password"
                        placeholder="Contraseña"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        style={{ padding: "10px", marginBottom: "10px", width: "200px" }}
                        required
                    />
                    <br />
                    <button type="submit" style={{ padding: "10px 20px", cursor: "pointer" }}>
                        Ingresar
                    </button>
                </form>
            ) : (
                <div>
                    <h2>Panel de Administración</h2>
                    <button onClick={handleLogout} style={{ marginBottom: "20px" }}>
                        Cerrar Sesión
                    </button>
                    <button onClick={() => navigate("/")} style={{ marginLeft: "10px" }}>
                        Volver a Inicio
                    </button>
                    {loading ? (
                        <p>Cargando reservas...</p>
                    ) : reservations.length > 0 ? (
                        <table>
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Cliente</th>
                                    <th>Habitación</th>
                                    <th>Check-In</th>
                                    <th>Check-Out</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {reservations.map((res) => (
                                    <tr key={res._id}>
                                        <td>{res._id}</td>
                                        <td>{res.clientName}</td>
                                        <td>{res.roomId}</td>
                                        <td>{new Date(res.checkIn).toISOString().split("T")[0]}</td>
                                        <td>{new Date(res.checkOut).toISOString().split("T")[0]}</td>
                                        <td>
                                            <button onClick={() => deleteReservation(res._id)}>Eliminar</button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>No hay reservas disponibles.</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default AdminPanel;
