import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import RoomList from '../components/RoomList';
import roomsData from '../data/roomsData';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';
import '../styles/Home.css';

registerLocale("es", es);

const Home = () => {
    const navigate = useNavigate();

    const today = new Date();
    const tomorrow = new Date(today.getTime() + 24 * 60 * 60 * 1000);

    const [checkIn, setCheckIn] = useState(today);
    const [checkOut, setCheckOut] = useState(tomorrow);
    const [availability, setAvailability] = useState({});
    const sortedRoomsData = [...roomsData].sort((a, b) => a.id - b.id);

    // Ajustar autom치ticamente la fecha de salida
    const handleCheckInChange = (date) => {
        setCheckIn(date);
        const nextDay = new Date(date.getTime() + 24 * 60 * 60 * 1000);
        setCheckOut(nextDay);
    };

    // L칩gica para verificar disponibilidad de todas las habitaciones
    useEffect(() => {
        const checkAllRoomsAvailability = async () => {
            if (!checkIn || !checkOut) return;

            const formattedCheckIn = checkIn.toISOString().split("T")[0];
            const formattedCheckOut = checkOut.toISOString().split("T")[0];

            try {
                const response = await fetch("http://localhost:5000/api/reservations/check-availability", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({
                        checkIn: formattedCheckIn,
                        checkOut: formattedCheckOut,
                    }),
                });

                const data = await response.json();
                const unavailableRoomIds = data.unavailableRooms || [];

                const updatedAvailability = {};
                sortedRoomsData.forEach((room) => {
                    updatedAvailability[room.id] = !unavailableRoomIds.includes(room.id);
                });

                setAvailability(updatedAvailability);
            } catch (error) {
                console.error("Error al verificar disponibilidad:", error);
            }
        };

        checkAllRoomsAvailability();
    }, [checkIn, checkOut]);

    return (
        <div className="home-container">
            <h1 className="home-title">Bienvenido al Hostal</h1>

            <div className="calendar-container">
                <label>Fecha de ingreso:</label>
                <DatePicker
                    selected={checkIn}
                    onChange={handleCheckInChange}
                    locale="es"
                    minDate={today}
                    dateFormat="dd/MM/yyyy"
                />

                <label>Fecha de salida:</label>
                <DatePicker
                    selected={checkOut}
                    onChange={(date) => date > checkIn && setCheckOut(date)}
                    locale="es"
                    minDate={new Date(checkIn.getTime() + 24 * 60 * 60 * 1000)}
                    dateFormat="dd/MM/yyyy"
                />
            </div>

            <div className="admin-button-container">
                <button onClick={() => navigate('/admin')} className="admin-button">
                    Administrador
                </button>
            </div>

            <RoomList rooms={sortedRoomsData} availability={availability} />
        </div>
    );
};

export default Home;
