import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const RoomList = ({ rooms }) => {
    const [selectedDate, setSelectedDate] = useState(null); // Fecha seleccionada en Inicio
    const [availability, setAvailability] = useState({}); // Estado de disponibilidad

    // Manejar cambio en la fecha seleccionada
    const handleDateChange = async (date) => {
        setSelectedDate(date);

        try {
            const response = await fetch('http://localhost:5000/api/check-availability', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ checkIn: date, checkOut: date }),
            });

            const data = await response.json();
            const unavailableRooms = data.unavailableRooms || [];
            const updatedAvailability = rooms.reduce((acc, room) => {
                acc[room.id] = !unavailableRooms.includes(room.id);
                return acc;
            }, {});
            setAvailability(updatedAvailability);
        } catch (error) {
            console.error("Error al verificar disponibilidad:", error);
        }
    };

    return (
        <div className="room-list">
            <h2>Selecciona una fecha para ver disponibilidad:</h2>
            <input
                type="date"
                onChange={(e) => handleDateChange(e.target.value)}
                style={{ marginBottom: "20px", padding: "5px" }}
            />
            {rooms.map((room) => (
                <div key={room.id} className="room-card">
                    <h2>{room.type}</h2>
                    <p className="room-availability">
                        Disponibilidad:{" "}
                        <span
                            className={
                                availability[room.id] === false ? "not-available" : "available"
                            }
                        >
                            {availability[room.id] === false ? "No disponible" : "Disponible"}
                        </span>
                    </p>
                    <Link to={`/room/${room.id}`} className="details-btn">
                        Ver Detalles
                    </Link>
                </div>
            ))}
        </div>
    );
};

export default RoomList;
