import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import rooms from '../data/rooms';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'; // Estilos del calendario
import es from "date-fns/locale/es"; // Localización en español

registerLocale("es", es); // Registrar idioma español

const RoomDetails = () => {
    const { id } = useParams(); // Obtén el ID de la habitación desde la URL
    const navigate = useNavigate(); // Para navegar de regreso
    const room = rooms.find((room) => room.id === parseInt(id)); // Busca la habitación por ID

    // Estado para el selector de personas (1 o 2 personas)
    const [selectedOption, setSelectedOption] = useState(1);

    // Estado para fechas de ingreso y salida
    const [checkIn, setCheckIn] = useState(null);
    const [checkOut, setCheckOut] = useState(null);

    // Estado para la disponibilidad
    const [isAvailable, setIsAvailable] = useState(false);

    // Estado para mostrar el formulario
    const [showForm, setShowForm] = useState(false);

    // Verificar disponibilidad cuando se seleccionan ambas fechas
    useEffect(() => {
        if (checkIn && checkOut) {
            const isRoomAvailable = Math.random() > 0.5; // Simulación de disponibilidad
            setIsAvailable(isRoomAvailable);
        }
    }, [checkIn, checkOut]);

    // Calcular precio total
    const calculateTotalPrice = () => {
        if (!checkIn || !checkOut) return 0;
        const nights = Math.ceil((checkOut - checkIn) / (1000 * 60 * 60 * 24)); // Días entre fechas
        const pricePerNight = selectedOption === 1 ? room.priceForOne : room.priceForTwo;
        return nights * pricePerNight;
    };

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            {/* Imagen de la habitación */}
            <img
                src={room.image}
                alt={room.type}
                style={{
                    width: '100%',
                    maxWidth: '600px',
                    borderRadius: '8px',
                    marginBottom: '20px',
                }}
            />

            {/* Información de la habitación */}
            <h1>{room.type}</h1>
            <p>
                Precio por noche:{" "}
                <strong>
                    {selectedOption === 1
                        ? `${room.priceForOne} CLP`
                        : `${room.priceForTwo || room.priceForOne} CLP`}
                </strong>
            </p>
            <label>
                Personas:{" "}
                <select
                    value={selectedOption}
                    onChange={(e) => setSelectedOption(parseInt(e.target.value))}
                    style={{
                        marginLeft: '10px',
                        padding: '5px',
                        fontSize: '14px',
                        borderRadius: '4px',
                        border: '1px solid #ddd',
                    }}
                >
                    <option value={1}>1 Persona</option>
                    {room.priceForTwo && <option value={2}>2 Personas</option>}
                </select>
            </label>

            {/* Fechas de ingreso y salida */}
            <div style={{ marginTop: '20px' }}>
                <h2>Selecciona tus fechas</h2>
                <div style={{ marginBottom: '10px' }}>
                    <p>Fecha de ingreso:</p>
                    <DatePicker
                        selected={checkIn}
                        onChange={(date) => setCheckIn(date)}
                        locale="es"
                        placeholderText="Selecciona fecha de ingreso"
                        minDate={new Date()}
                    />
                </div>
                <div style={{ marginBottom: '10px' }}>
                    <p>Fecha de salida:</p>
                    <DatePicker
                        selected={checkOut}
                        onChange={(date) => setCheckOut(date)}
                        locale="es"
                        placeholderText="Selecciona fecha de salida"
                        minDate={checkIn || new Date()}
                    />
                </div>

                {/* Mensaje de disponibilidad */}
                {checkIn && checkOut && (
                    <p style={{ color: isAvailable ? "green" : "red", fontWeight: "bold" }}>
                        {isAvailable ? "¡Disponible!" : "No disponible"}
                    </p>
                )}
            </div>

            {/* Mostrar formulario si está disponible */}
            {isAvailable && !showForm && (
                <button
                    onClick={() => setShowForm(true)}
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#28a745',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        fontSize: '16px',
                        marginTop: '20px',
                    }}
                >
                    Reservar
                </button>
            )}

            {showForm && (
                <div style={{ marginTop: '20px', textAlign: 'left', maxWidth: '600px', margin: '0 auto' }}>
                    <h3>Formulario de Reserva</h3>
                    <form>
                        <label>Nombre completo:</label>
                        <input type="text" required style={{ width: '100%', marginBottom: '10px' }} />
                        {selectedOption === 2 && (
                            <>
                                <label>Nombre completo (persona 2):</label>
                                <input type="text" required style={{ width: '100%', marginBottom: '10px' }} />
                            </>
                        )}
                        <label>RUT o número de pasaporte:</label>
                        <input type="text" required style={{ width: '100%', marginBottom: '10px' }} />
                        <label>Teléfono:</label>
                        <input type="text" required style={{ width: '100%', marginBottom: '10px' }} />
                        <label>Correo electrónico (opcional):</label>
                        <input type="email" style={{ width: '100%', marginBottom: '10px' }} />
                        <label>Nacionalidad:</label>
                        <input type="text" required style={{ width: '100%', marginBottom: '10px' }} />
                        <label>Dirección:</label>
                        <input type="text" required style={{ width: '100%', marginBottom: '10px' }} />

                        <p>
                            Precio total: <strong>{calculateTotalPrice()} CLP</strong>
                        </p>
                        <button
                            type="submit"
                            style={{
                                padding: '10px 20px',
                                backgroundColor: '#007bff',
                                color: '#fff',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer',
                                fontSize: '16px',
                                marginTop: '10px',
                            }}
                            onClick={(e) => {
                                e.preventDefault();
                                alert('Reserva realizada exitosamente');
                                navigate('/');
                            }}
                        >
                            Confirmar Reserva
                        </button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default RoomDetails;
