import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RoomList = ({ rooms }) => {
    const [unavailableRooms, setUnavailableRooms] = useState([]);

    const checkAvailability = async (checkIn, checkOut) => {
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/reservations/check-availability`, {
                checkIn,
                checkOut,
            });
            setUnavailableRooms(response.data.unavailableRooms);
        } catch (error) {
            console.error('Error al verificar disponibilidad:', error.response?.data || error.message);
        }
    };

    useEffect(() => {
        // Ejemplo: Fechas estáticas para la prueba, las cambiarás por el calendario dinámico
        checkAvailability("2024-12-20", "2024-12-25");
    }, []);

    return (
        <div className="rooms-list">
            {rooms.map((room) => (
                <div key={room.id} className="room-card">
                    <h3>{room.type}</h3>
                    <p>ID: {room.id}</p>
                    <p>Precio: ${room.priceForOne} CLP</p>
                    <button
                        disabled={unavailableRooms.includes(room.id)}
                        style={{
                            backgroundColor: unavailableRooms.includes(room.id) ? 'red' : 'green',
                        }}
                    >
                        {unavailableRooms.includes(room.id) ? 'No disponible' : 'Reservar'}
                    </button>
                </div>
            ))}
        </div>
    );
};

export default RoomList;
