import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Para la navegación

const RoomList = ({ rooms }) => {
    // Crear una copia ordenada de las habitaciones
    const sortedRooms = [...rooms].sort((a, b) => a.id - b.id);

    // Estado para manejar los nombres de las habitaciones
    const [roomNames, setRoomNames] = useState(
        rooms.reduce((acc, room) => ({ ...acc, [room.id]: room.type }), {})
    );

    // Estado para controlar la selección de personas (1 o 2 personas)
    const [selectedOptions, setSelectedOptions] = useState(
        rooms.reduce((acc, room) => ({ ...acc, [room.id]: 1 }), {})
    );

    // Manejar cambio en el nombre de la habitación
    const handleNameChange = (id, newName) => {
        setRoomNames((prev) => ({ ...prev, [id]: newName }));
    };

    // Manejar cambio en el selector de personas
    const handleSelectChange = (id, value) => {
        setSelectedOptions((prev) => ({ ...prev, [id]: value }));
    };

    return (
        <div className="room-list">
            {sortedRooms.map((room) => (
                <div key={room.id} className="room-card">
                    {/* Imagen de la habitación */}
                    <img src={room.image} alt={roomNames[room.id]} className="room-image" />

                    {/* Nombre editable de la habitación */}
                    <h2>
                        <input
                            type="text"
                            value={roomNames[room.id]} // Muestra el nombre actual
                            onChange={(e) => handleNameChange(room.id, e.target.value)} // Cambiar nombre
                            className="room-name-input"
                        />
                    </h2>

                    {/* Precio dinámico */}
                    <p>
                        Precio:{" "}
                        <strong>
                            {selectedOptions[room.id] === 1
                                ? `${room.priceForOne} CLP`
                                : `${room.priceForTwo || room.priceForOne} CLP`}
                        </strong>
                    </p>

                    {/* Selector de personas */}
                    <label>
                        Personas:
                        <select
                            value={selectedOptions[room.id]}
                            onChange={(e) => handleSelectChange(room.id, parseInt(e.target.value))}
                        >
                            <option value={1}>1 Persona</option>
                            {room.priceForTwo && <option value={2}>2 Personas</option>}
                        </select>
                    </label>

                    {/* Disponibilidad */}
                    <p className="room-availability">
                        Disponibilidad:{" "}
                        <span className={room.available ? "available" : "not-available"}>
                            {room.available ? "Disponible" : "No disponible"}
                        </span>
                    </p>

                    {/* Botón Ver Detalles */}
                    <Link to={`/room/${room.id}`} className="details-btn">
                        Ver Detalles
                    </Link>
                </div>
            ))}
        </div>
    );
};

export default RoomList;
