import React, { useContext } from 'react';
import roomsData from '../data/roomsData';
import RoomList from '../components/RoomList';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import '../styles/Home.css';
import es from "date-fns/locale/es";
import { ReservationContext } from '../ReservationContext'; // Importar el contexto

registerLocale("es", es);

const Home = () => {
    const { selectedDate, setSelectedDate, availability, setAvailability } = useContext(ReservationContext);

    const handleDateChange = async (date) => {
        setSelectedDate(date);

        try {
            const response = await fetch("http://localhost:5000/api/reservations/check-availability", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    checkIn: date?.toISOString().split("T")[0],
                    checkOut: date?.toISOString().split("T")[0],
                }),
            });

            const data = await response.json();
            const unavailableRooms = data.unavailableRooms || [];

            // Actualizar disponibilidad
            const updatedAvailability = roomsData.reduce((acc, room) => {
                acc[room.id] = !unavailableRooms.includes(room.id);
                return acc;
            }, {});

            setAvailability(updatedAvailability);
        } catch (error) {
            console.error("Error al verificar disponibilidad:", error);
        }
    };

    return (
        <div className="home-container">
            <h1>Bienvenido al Hostal</h1>

            {/* Calendario sincronizado */}
            <div className="calendar-container">
                <p>Selecciona una fecha para ver disponibilidad:</p>
                <DatePicker
                    selected={selectedDate}
                    onChange={handleDateChange}
                    locale="es"
                    placeholderText="Selecciona una fecha"
                    inline
                />
            </div>

            {/* Lista de habitaciones */}
            <RoomList rooms={roomsData} availability={availability} />
        </div>
    );
};

export default Home;
