import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const RoomList = ({ rooms, availability, selectedDate, handleDateChange }) => {
    // Crear una copia ordenada de las habitaciones
    const sortedRooms = [...rooms].sort((a, b) => a.id - b.id);

    // Estado para controlar la selección de personas (1 o 2 personas)
    const [selectedOptions, setSelectedOptions] = useState(
        rooms.reduce((acc, room) => ({ ...acc, [room.id]: 1 }), {})
    );

    // Manejar cambio en el selector de personas
    const handleSelectChange = (id, value) => {
        setSelectedOptions((prev) => ({ ...prev, [id]: value }));
    };

    return (
        <div className="room-list">
            <h2>Selecciona una fecha para ver dispolidad:</h2>
            <input
                type="date"
                onChange={handleDateChange}
                value={selectedDate || ""}
                style={{ marginBottom: "20px", padding: "5px", width: "100%", maxWidth: "300px" }}
            />
            {sortedRooms.map((room) => (
                <div key={room.id} className="room-card">
                    {/* Imagen de la habitación */}
                    <img src={room.image} alt={room.type} className="room-image" />

                    {/* ID de la habitación */}
                    <p className="room-id">
                        ID: <strong>{room.id}</strong>
                    </p>

                    {/* Nombre de la habitación */}
                    <h2>{room.type}</h2>

                    {/* Precio dinámico */}
                    <p>
                        Precio:{" "}
                        <strong>
                            {selectedOptions[room.id] === 1
                                ? `${room.priceForOne} CLP`
                                : `${room.priceForTwo || room.priceForOne} CLP`}
                        </strong>
                    </p>

                    {/* Selector de personas (excepto para IDs 4, 5, y 8) */}
                    {![4, 5, 8].includes(room.id) && (
                        <label>
                            Personas:
                            <select
                                value={selectedOptions[room.id]}
                                onChange={(e) => handleSelectChange(room.id, parseInt(e.target.value))}
                            >
                                <option value={1}>1 Persona</option>
                                {room.priceForTwo && <option value={2}>2 Personas</option>}
                            </select>
                        </label>
                    )}

                    {/* Disponibilidad */}
                    <p className="room-availability">
                        Disponibilidad:{" "}
                        <span className={availability[room.id] === false ? "not-available" : "available"}>
                            {availability[room.id] === false ? "No disponible" : "Disponible"}
                        </span>
                    </p>

                    {/* Botón Ver Detalles */}
                    <Link to={`/room/${room.id}`} className="details-btn">
                        Ver Detalles
                    </Link>
                </div>
            ))}
        </div>
    );
};

export default RoomList;
