import React, { useState, useEffect } from 'react';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';
import roomsData from '../data/roomsData';
import '../styles/Home.css';
import Footer from '../components/Footer';

registerLocale("es", es);

const Home = () => {
    const today = new Date();
    const tomorrow = new Date(today.getTime() + 24 * 60 * 60 * 1000);

    const [checkIn, setCheckIn] = useState(today);
    const [checkOut, setCheckOut] = useState(tomorrow);
    const [availability, setAvailability] = useState({});
    const [selectedOptions, setSelectedOptions] = useState(
        roomsData.reduce((acc, room) => ({ ...acc, [room.id]: 1 }), {})
    );

    // Función para verificar disponibilidad
    const calculateAvailability = async () => {
        const updatedAvailability = {};

        for (const room of roomsData) {
            try {
                const response = await fetch("https://hostaljallalla2.onrender.com/api/reservations/check-availability", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({
                        roomId: room.id,
                        checkIn: checkIn.toISOString().split("T")[0],
                        checkOut: checkOut.toISOString().split("T")[0],
                    }),
                });

                const data = await response.json();
                updatedAvailability[room.id] = !data.unavailableRooms.includes(room.id);
            } catch (error) {
                console.error(`Error al verificar disponibilidad para la habitación ${room.id}:`, error);
                updatedAvailability[room.id] = false;
            }
        }

        setAvailability(updatedAvailability);
    };

    // Recalcular disponibilidad al cambiar fechas
    useEffect(() => {
        if (checkIn && checkOut) {
            calculateAvailability();
        }
    }, [checkIn, checkOut]);

    const handleSelectChange = (id, value) => {
        setSelectedOptions((prev) => ({ ...prev, [id]: value }));
    };

    return (
        <div className="home-container">
            <h1 className="home-title">Bienvenido al Hostal Jallalla</h1>

            {/* Calendarios visibles */}
            <div className="calendar-container">
                <div>
                    <label>Fecha de ingreso:</label>
                    <DatePicker
                        selected={checkIn}
                        onChange={(date) => {
                            setCheckIn(date);

                            // Ajustar checkOut automáticamente si es necesario
                            if (date >= checkOut) {
                                const nextDay = new Date(date.getTime() + 24 * 60 * 60 * 1000);
                                setCheckOut(nextDay);
                            }
                        }}
                        locale="es"
                        minDate={today}
                        dateFormat="dd/MM/yyyy"
                    />
                </div>

                <div>
                    <label>Fecha de salida:</label>
                    <DatePicker
                        selected={checkOut}
                        onChange={(date) => {
                            if (date > checkIn) {
                                setCheckOut(date);
                            }
                        }}
                        locale="es"
                        minDate={new Date(checkIn.getTime() + 24 * 60 * 60 * 1000)}
                        dateFormat="dd/MM/yyyy"
                    />
                </div>
            </div>

            {/* Renderizado de habitaciones */}
            <div className="room-list">
                {roomsData.map((room) => (
                    <div key={room.id} className="room-card">
                        <img src={room.image} alt={room.type} className="room-image" />
                        <p className="room-id">ID: <strong>{room.id}</strong></p>
                        <h2>{room.type}</h2>

                        <p>
                            Precio: <strong>
                                {selectedOptions[room.id] === 2 && room.priceForTwo
                                    ? `${room.priceForTwo} CLP`
                                    : `${room.priceForOne} CLP`}
                            </strong>
                        </p>

                        <label>
                            Personas:
                            <select
                                value={selectedOptions[room.id] || 1}
                                onChange={(e) => handleSelectChange(room.id, parseInt(e.target.value))}
                            >
                                <option value={1}>1 Persona</option>
                                {room.priceForTwo && <option value={2}>2 Personas</option>}
                            </select>
                        </label>

                        <p className="room-availability">
                            <span className={availability[room.id] ? "available" : "not-available"}>
                                {availability[room.id] ? "Disponible" : "No disponible"}
                            </span>
                        </p>

                        <button
                            disabled={!availability[room.id]}
                            className={`details-btn ${availability[room.id] ? "" : "disabled"}`}
                            style={{
                                pointerEvents: availability[room.id] ? "auto" : "none",
                                backgroundColor: availability[room.id] ? "#007bff" : "#ccc",
                                cursor: availability[room.id] ? "pointer" : "not-allowed",
                            }}
                        >
                            {availability[room.id] ? "Ver Detalles" : "No disponible"}
                        </button>
                    </div>
                ))}
            </div>

            <Footer />
        </div>
    );
};

export default Home;
