import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import rooms from '../data/rooms';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';

registerLocale("es", es);

const RoomDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const room = rooms.find((room) => room.id === parseInt(id));

    const [selectedDate, setSelectedDate] = useState(null); // Fecha de ingreso
    const [checkOut, setCheckOut] = useState(null); // Fecha de salida
    const [isAvailable, setIsAvailable] = useState(false); // Disponibilidad de la habitación
    const [selectedOption, setSelectedOption] = useState(1); // Número de personas
    const [showForm, setShowForm] = useState(false); // Mostrar formulario de reserva
    const [error, setError] = useState(''); // Error si fechas coinciden

    const [formData, setFormData] = useState({
        clientName1: "", // Nombre completo de Persona 1
        clientName2: "", // Nombre completo de Persona 2 (opcional si hay 2 personas)
        clientEmail: "", // Correo electrónico (opcional)
        rutOrPassport: "", // RUT o número de pasaporte
        phone: "",
        nationality: "",
        address: "",
    });

    // Verificar disponibilidad conectando con el backend
    useEffect(() => {
        const checkAvailability = async () => {
            if (selectedDate && checkOut) {
                if (selectedDate.toISOString().split("T")[0] === checkOut.toISOString().split("T")[0]) {
                    setError("La fecha de ingreso no puede ser igual a la de salida.");
                    setIsAvailable(false);
                    return;
                }

                try {
                    const response = await fetch("http://localhost:5000/api/reservations/check-availability", {
                        method: "POST",
                        headers: { "Content-Type": "application/json" },
                        body: JSON.stringify({
                            checkIn: selectedDate.toISOString().split("T")[0],
                            checkOut: checkOut.toISOString().split("T")[0],
                            roomId: room.id,
                        }),
                    });

                    const data = await response.json();
                    setError(""); // Limpiar errores si las fechas son válidas
                    setIsAvailable(!data.unavailableRooms?.includes(room.id));
                } catch (error) {
                    console.error("Error al verificar disponibilidad:", error);
                    setIsAvailable(false);
                }
            }
        };

        checkAvailability();
    }, [selectedDate, checkOut, room.id]);

    // Manejar cambios en el formulario
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    // Manejar la selección de fecha de ingreso
    const handleCheckInChange = (date) => {
        setSelectedDate(date);

        // Si la fecha de salida es anterior a la nueva fecha de ingreso o no existe
        if (!checkOut || date >= checkOut) {
            const nextDay = new Date(date.getTime() + 24 * 60 * 60 * 1000); // Sumar un día
            setCheckOut(nextDay); // Seleccionar automáticamente la nueva fecha de salida
        }
    };

    // Manejar la selección de fecha de salida
    const handleCheckOutChange = (date) => {
        setCheckOut(date);
    };

    // Calcular cantidad de noches
    const calculateNights = () => {
        if (!selectedDate || !checkOut) return 0;
        return Math.ceil((checkOut - selectedDate) / (1000 * 60 * 60 * 24));
    };

    // Calcular precio total
    const calculateTotalPrice = () => {
        const nights = calculateNights();
        const pricePerNight = selectedOption === 1 ? room.priceForOne : room.priceForTwo;
        return nights * pricePerNight;
    };

    // Enviar la reserva al backend
    const handleConfirmReservation = async (e) => {
        e.preventDefault();
    
        const reservationData = {
            roomId: room.id,
            clientName: formData.clientName1, // Cambiado de clientName1 a clientName
            clientName2: selectedOption === 2 ? formData.clientName2 : undefined, // Solo agregar si selecciona 2 personas
            clientEmail: formData.clientEmail || undefined, // Opcional
            rutOrPassport: formData.rutOrPassport, // Obligatorio
            numberOfGuests: selectedOption,
            checkIn: selectedDate?.toISOString().split("T")[0],
            checkOut: checkOut?.toISOString().split("T")[0],
            phone: formData.phone,
            nationality: formData.nationality,
            address: formData.address,
            nights: calculateNights(), // Nuevo campo: cantidad de noches calculadas
            totalCost: calculateTotalPrice(), // Nuevo campo: costo total calculado
        };
    
        console.log("Datos enviados al backend:", reservationData); // Depuración
    
        try {
            const response = await fetch("http://localhost:5000/api/reservations", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(reservationData),
            });
    
            if (response.ok) {
                // Redirigir a la página de éxito y pasar los datos de la reserva
                navigate('/success', { state: { reservation: reservationData } });
            } else {
                const result = await response.json();
                alert(`Error al realizar la reserva: ${result.message}`);
            }
        } catch (error) {
            console.error("Error al conectar con el servidor:", error);
            alert("Hubo un error al conectar con el servidor.");
        }
    };
    


    return (
        <div style={{ padding: "80px", textAlign: "center" }}>
            <h1>{room.type}</h1>
            <p><strong>ID:</strong> {room.id}</p>
            <img
                src={room.image}
                alt={room.type}
                style={{
                    width: "100%",
                    maxWidth: "200px",
                    borderRadius: "8px",
                    marginBottom: "20px",
                }}
            />

            <p>
                Precio por noche: {" "}
                <strong>
                    {selectedOption === 1
                        ? `${room.priceForOne} CLP`
                        : `${room.priceForTwo || room.priceForOne} CLP`}
                </strong>
            </p>
            <label>
                Personas: {" "}
                <select
                    value={selectedOption}
                    onChange={(e) => setSelectedOption(parseInt(e.target.value))}
                    style={{
                        marginLeft: "10px",
                        padding: "5px",
                        fontSize: "14px",
                        borderRadius: "4px",
                        border: "1px solid #ddd",
                    }}
                >
                    <option value={1}>1 Persona</option>
                    {room.priceForTwo && <option value={2}>2 Personas</option>}
                </select>
            </label>
            <div style={{ marginBottom: "20px" }}>
                <button
                    onClick={() => navigate(-1)}
                    style={{
                        padding: "10px 20px",
                        backgroundColor: "#007bff",
                        color: "#fff",
                        border: "none",
                        borderRadius: "2px",
                        cursor: "pointer",
                        fontSize: "16px",
                        marginRight: "10px",
                    }}
                >
                    Volver
                </button>
                <button
                    disabled={!selectedDate || !checkOut || !isAvailable}
                    onClick={() => setShowForm(true)}
                    style={{
                        padding: "12px 20px",
                        backgroundColor: !selectedDate || !checkOut || !isAvailable ? "#ccc" : "#28a745",
                        color: !selectedDate || !checkOut || !isAvailable ? "#666" : "#fff",
                        border: "none",
                        borderRadius: "20px",
                        cursor: !selectedDate || !checkOut || !isAvailable ? "not-allowed" : "pointer",
                        fontSize: "16px",
                    }}
                >
                    Reservar
                </button>
            </div>

            <div style={{ marginTop: "20px" }}>
                <h2>Selecciona tus fechas</h2>
                <div style={{ marginBottom: "10px" }}>
                    <p>Fecha de ingreso:</p>
                    <DatePicker
                        selected={selectedDate}
                        onChange={handleCheckInChange} // Sincronización automática
                        locale="es"
                        placeholderText="Selecciona fecha de ingreso"
                        minDate={new Date()} // No permitir fechas pasadas
                        dateFormat="dd/MM/yyyy"
                    />
                    <p>Fecha de salida:</p>
                    <DatePicker
                        selected={checkOut}
                        onChange={handleCheckOutChange} // Actualizar manualmente
                        locale="es"
                        placeholderText="Selecciona fecha de salida"
                        minDate={selectedDate ? new Date(selectedDate.getTime() + 24 * 60 * 60 * 1000) : new Date()} // Mínimo un día después
                        dateFormat="dd/MM/yyyy"
                    />
                </div>

                {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}

                {selectedDate && checkOut && !error && (
                    <p style={{ color: isAvailable ? "green" : "red", fontWeight: "bold" }}>
                        {isAvailable ? "¡Disponible!" : "No disponible"}
                    </p>
                )}
            </div>

            {showForm && (
                <form
                    onSubmit={handleConfirmReservation}
                    style={{
                        textAlign: "left",
                        maxWidth: "800px",
                        margin: "50px auto",
                    }}
                >
                    <h3>Formulario de Reserva</h3>
                    <label>Nombre completo (Persona 1):</label>
                    <input
                        type="text"
                        name="clientName1"
                        value={formData.clientName1}
                        onChange={handleInputChange}
                        required
                        style={{ width: "100%", marginBottom: "10px" }}
                    />

                    {selectedOption === 2 && (
                        <>
                            <label>Nombre completo (Persona 2):</label>
                            <input
                                type="text"
                                name="clientName2"
                                value={formData.clientName2}
                                onChange={handleInputChange}
                                required
                                style={{ width: "100%", marginBottom: "10px" }}
                            />
                        </>
                    )}

                    <label>RUT o número de pasaporte:</label>
                    <input
                        type="text"
                        name="rutOrPassport"
                        value={formData.rutOrPassport}
                        onChange={handleInputChange}
                        required
                        style={{ width: "100%", marginBottom: "10px" }}
                    />

                    <label>Correo electrónico (opcional):</label>
                    <input
                        type="email"
                        name="clientEmail"
                        value={formData.clientEmail}
                        onChange={handleInputChange}
                        style={{ width: "100%", marginBottom: "10px" }}
                    />

                    <label>Teléfono:</label>
                    <input
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleInputChange}
                        required
                        style={{ width: "100%", marginBottom: "10px" }}
                    />

                    <label>Nacionalidad:</label>
                    <input
                        type="text"
                        name="nationality"
                        value={formData.nationality}
                        onChange={handleInputChange}
                        required
                        style={{ width: "100%", marginBottom: "10px" }}
                    />

                    <label>Dirección:</label>
                    <input
                        type="text"
                        name="address"
                        value={formData.address}
                        onChange={handleInputChange}
                        required
                        style={{ width: "100%", marginBottom: "10px" }}
                    />

                    <p>
                        Cantidad de noches: <strong>{calculateNights()}</strong>
                    </p>
                    <p>
                        Precio total: <strong>{calculateTotalPrice()} CLP</strong>
                    </p>
                    <button
                        type="submit"
                        style={{
                            padding: "5px 332px",
                            backgroundColor: "#007bff",
                            color: "#fff",
                            border: "none",
                            borderRadius: "20px",
                            cursor: "pointer",
                            fontSize: "15px",
                            marginTop: "10px",
                        }}
                    >
                        Confirmar Reserva
                    </button>
                </form>
            )}
        </div>
    );
};

export default RoomDetails;
