import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import rooms from '../data/rooms';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';

registerLocale("es", es);

const RoomDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const room = rooms.find((room) => room.id === parseInt(id));

    const [selectedOption, setSelectedOption] = useState(1);
    const [checkIn, setCheckIn] = useState(null);
    const [checkOut, setCheckOut] = useState(null);
    const [isAvailable, setIsAvailable] = useState(false);
    const [showForm, setShowForm] = useState(false);

    // Estado para los datos del formulario
    const [formData, setFormData] = useState({
        clientName: "",
        clientEmail: "",
        phone: "",
        nationality: "",
        address: "",
    });

    // Verificar disponibilidad cuando se seleccionan ambas fechas
    useEffect(() => {
        if (checkIn && checkOut) {
            const isRoomAvailable = Math.random() > 0.5; // Simulación de disponibilidad
            setIsAvailable(isRoomAvailable);
        }
    }, [checkIn, checkOut]);

    // Manejar cambio en los campos del formulario
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    // Calcular precio total
    const calculateTotalPrice = () => {
        if (!checkIn || !checkOut) return 0;
        const nights = Math.ceil((checkOut - checkIn) / (1000 * 60 * 60 * 24));
        const pricePerNight = selectedOption === 1 ? room.priceForOne : room.priceForTwo;
        return nights * pricePerNight;
    };

    // Enviar reserva al backend
    const handleConfirmReservation = async (e) => {
        e.preventDefault();

        const reservationData = {
            roomId: room.id,
            clientName: formData.clientName,
            clientEmail: formData.clientEmail,
            numberOfGuests: selectedOption,
            checkIn: checkIn?.toISOString().split("T")[0],
            checkOut: checkOut?.toISOString().split("T")[0],
            phone: formData.phone,
            nationality: formData.nationality,
            address: formData.address,
        };

        try {
            const response = await fetch("http://localhost:5000/api/reservations", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(reservationData),
            });

            const result = await response.json();
            if (response.ok) {
                alert("Reserva realizada exitosamente");
                navigate("/");
            } else {
                alert(`Error al realizar la reserva: ${result.message}`);
            }
        } catch (error) {
            console.error("Error al conectar con el servidor:", error);
            alert("Hubo un error al conectar con el servidor.");
        }
    };

    return (
        <div style={{ padding: "20px", textAlign: "center" }}>
            <h1>{room.type}</h1>
            <img
                src={room.image}
                alt={room.type}
                style={{
                    width: "100%",
                    maxWidth: "600px",
                    borderRadius: "8px",
                    marginBottom: "20px",
                }}
            />

            <div style={{ marginTop: "20px" }}>
                <h2>Selecciona tus fechas</h2>
                <div style={{ marginBottom: "10px" }}>
                    <p>Fecha de ingreso:</p>
                    <DatePicker
                        selected={checkIn}
                        onChange={(date) => setCheckIn(date)}
                        locale="es"
                        placeholderText="Selecciona fecha de ingreso"
                        minDate={new Date()}
                    />
                </div>
                <div style={{ marginBottom: "10px" }}>
                    <p>Fecha de salida:</p>
                    <DatePicker
                        selected={checkOut}
                        onChange={(date) => setCheckOut(date)}
                        locale="es"
                        placeholderText="Selecciona fecha de salida"
                        minDate={checkIn || new Date()}
                    />
                </div>

                {isAvailable && !showForm && (
                    <button
                        onClick={() => setShowForm(true)}
                        style={{
                            padding: "10px 20px",
                            backgroundColor: "#28a745",
                            color: "#fff",
                            border: "none",
                            borderRadius: "4px",
                            cursor: "pointer",
                            fontSize: "16px",
                            marginTop: "20px",
                        }}
                    >
                        Reservar
                    </button>
                )}
            </div>

            {showForm && (
                <form
                    onSubmit={handleConfirmReservation}
                    style={{ textAlign: "left", maxWidth: "600px", margin: "20px auto" }}
                >
                    <h3>Formulario de Reserva</h3>
                    <label>Nombre completo:</label>
                    <input
                        type="text"
                        name="clientName"
                        value={formData.clientName}
                        onChange={handleInputChange}
                        required
                        style={{ width: "100%", marginBottom: "10px" }}
                    />

                    <label>Correo electrónico:</label>
                    <input
                        type="email"
                        name="clientEmail"
                        value={formData.clientEmail}
                        onChange={handleInputChange}
                        style={{ width: "100%", marginBottom: "10px" }}
                    />

                    <label>Teléfono:</label>
                    <input
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleInputChange}
                        required
                        style={{ width: "100%", marginBottom: "10px" }}
                    />

                    <label>Nacionalidad:</label>
                    <input
                        type="text"
                        name="nationality"
                        value={formData.nationality}
                        onChange={handleInputChange}
                        required
                        style={{ width: "100%", marginBottom: "10px" }}
                    />

                    <label>Dirección:</label>
                    <input
                        type="text"
                        name="address"
                        value={formData.address}
                        onChange={handleInputChange}
                        required
                        style={{ width: "100%", marginBottom: "10px" }}
                    />

                    <p>
                        Precio total: <strong>{calculateTotalPrice()} CLP</strong>
                    </p>
                    <button
                        type="submit"
                        style={{
                            padding: "10px 20px",
                            backgroundColor: "#007bff",
                            color: "#fff",
                            border: "none",
                            borderRadius: "4px",
                            cursor: "pointer",
                            fontSize: "16px",
                            marginTop: "10px",
                        }}
                    >
                        Confirmar Reserva
                    </button>
                </form>
            )}
        </div>
    );
};

export default RoomDetails;
