import React, { useState, useRef, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Header.css'; // Asegúrate de que la ruta sea correcta
import headerImage from '../images/header-background.jpg'; // Asegúrate de que la ruta sea correcta

const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);
    const navigate = useNavigate();
    const menuRef = useRef(null); // Ref para el menú

    // Cierra el menú si se hace clic fuera de él
    useEffect(() => {
        const handleClickOutside = (event) => {
            // Solo cierra el menú si se hace clic fuera del área del menú (incluyendo el botón)
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setMenuOpen(false);
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [menuRef]);

    const handleLinkClick = (path) => {
        if (path) {
            navigate(path);
        }
        setMenuOpen(false); // Cierra el menú al hacer clic en cualquier opción
    };

    return (
        <header
            className="header-container"
            style={{ backgroundImage: `url(${headerImage})` }}
        >
            {/* Logo a la izquierda */}
            <Link to="/" className="logo-container" onClick={() => handleLinkClick('/')}>
                <img src="/images/logo.png" alt="Logo del Hostal" className="logo" />
                <span className="logo-text">Hostal Jallalla</span>
            </Link>

            {/* Navegación de Escritorio (se oculta en móvil) */}
            <nav className="desktop-nav">
                <ul className="nav-links">
                    <li><Link to="/" className="nav-button">Inicio</Link></li>
                    <li><Link to="/nosotros" className="nav-button">Nosotros</Link></li>
                    <li><Link to="/galeria" className="nav-button">Galería</Link></li>
                    <li>
                        <button className="admin-button" onClick={() => navigate('/admin')}>
                            Administrador
                        </button>
                    </li>
                </ul>
            </nav>

            {/* Contenedor para el menú móvil */}
            <div className="mobile-menu" ref={menuRef}>
                <button className="menu-toggle" onClick={() => setMenuOpen(!menuOpen)}>
                    <span className="hamburger-line"></span>
                    <span className="hamburger-line"></span>
                    <span className="hamburger-line"></span>
                </button>

                {/* El menú desplegable ahora contiene TODOS los enlaces */}
                {menuOpen && (
                    <div className="dropdown-menu">
                        <ul>
                            <li><Link to="/" className="nav-button" onClick={() => handleLinkClick('/')}>Inicio</Link></li>
                            <li><Link to="/nosotros" className="nav-button" onClick={() => handleLinkClick('/nosotros')}>Nosotros</Link></li>
                            <li><Link to="/galeria" className="nav-button" onClick={() => handleLinkClick('/galeria')}>Galería</Link></li>
                            <li>
                                <button className="admin-button" onClick={() => handleLinkClick('/admin')}>
                                    Administrador
                                </button>
                            </li>
                        </ul>
                    </div>
                )}
            </div>
        </header>
    );
};

export default Header;
