// Home.js actualizado con integración correcta
import React, { useState, useEffect } from 'react';
import RoomList from '../components/RoomList';
import roomsData from '../data/roomsData';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';
import '../styles/Home.css';

registerLocale("es", es);

const Home = () => {
    const [checkIn, setCheckIn] = useState(null); // Fecha de ingreso
    const [checkOut, setCheckOut] = useState(null); // Fecha de salida
    const [availability, setAvailability] = useState({}); // Disponibilidad de habitaciones

    // Ordenar habitaciones por ID antes de renderizar
    const sortedRooms = roomsData.sort((a, b) => a.id - b.id);

    // Función para verificar disponibilidad en el backend
    const fetchAvailability = async () => {
        if (!checkIn || !checkOut) return; // No hacer nada si faltan fechas
        const formattedCheckIn = checkIn.toISOString().split("T")[0];
        const formattedCheckOut = checkOut.toISOString().split("T")[0];
        console.log("Rango de fechas seleccionado:", { checkIn: formattedCheckIn, checkOut: formattedCheckOut }); // Depuración

        try {
            const response = await fetch("http://localhost:5000/api/reservations/check-availability", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    checkIn: formattedCheckIn,
                    checkOut: formattedCheckOut,
                }),
            });

            const data = await response.json();
            console.log("Respuesta del backend:", data); // Depuración

            // Procesar datos para actualizar disponibilidad
            const updatedAvailability = sortedRooms.reduce((acc, room) => {
                acc[room.id] = !data.unavailableRooms?.includes(room.id); // Disponible si no está en unavailableRooms
                return acc;
            }, {});

            console.log("Disponibilidad actualizada:", updatedAvailability); // Depuración
            setAvailability(updatedAvailability); // Actualizar estado global de disponibilidad
        } catch (error) {
            console.error("Error al verificar disponibilidad:", error);
        }
    };

    // Llamar a fetchAvailability cuando cambian las fechas seleccionadas
    useEffect(() => {
        if (checkIn && checkOut) {
            fetchAvailability();
        }
    }, [checkIn, checkOut]);

    return (
        <div className="home-container">
            <h1 className="home-title">Bienvenido al Hostal Jallalla</h1>

            {/* Calendarios para seleccionar rango de fechas */}
            <div className="calendar-container">
                <label>
                    Selecciona tus fechas
                </label>
                <label>
                    Ingreso________Salida
                </label>
                <div className="datepicker-wrapper">
                    {/* Fecha de ingreso */}
                    <DatePicker
                        selected={checkIn}
                        onChange={(date) => {
                            setCheckIn(date);
                            if (checkOut && date >= checkOut) {
                                setCheckOut(null); // Resetear fecha de salida si es inválida
                            }
                        }}
                        locale="es"
                        placeholderText="Fecha de ingreso"
                        minDate={new Date()}
                        dateFormat="dd/MM/yyyy"
                        className="datepicker-input"
                    />

                    {/* Fecha de salida */}
                    <DatePicker
                        selected={checkOut}
                        onChange={(date) => setCheckOut(date)}
                        locale="es"
                        placeholderText="Fecha de salida"
                        minDate={checkIn ? new Date(checkIn.getTime() + 24 * 60 * 60 * 1000) : new Date()}
                        dateFormat="dd/MM/yyyy"
                        className="datepicker-input"
                    />
                </div>
            </div>

            {/* Lista de habitaciones */}
            <RoomList rooms={sortedRooms} availability={availability} />
        </div>
    );
};

export default Home;
