import React, { useState, useEffect } from 'react';
import RoomList from '../components/RoomList';
import roomsData from '../data/roomsData';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import '../styles/Home.css';
import es from 'date-fns/locale/es';

registerLocale("es", es);

const Home = () => {
    const [selectedDate, setSelectedDate] = useState(null); // Fecha seleccionada en el calendario
    const [availability, setAvailability] = useState({}); // Estado de disponibilidad de las habitaciones

    // Función para verificar disponibilidad en el backend
    const fetchAvailability = async (date) => {
        if (!date) return; // No hacer nada si no hay fecha seleccionada
        const formattedDate = date.toISOString().split("T")[0];
        console.log("Fecha seleccionada:", formattedDate); // Depuración: Fecha enviada al backend

        try {
            const response = await fetch("http://localhost:5000/api/reservations/check-availability", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    checkIn: formattedDate,
                    checkOut: formattedDate, // Solo un día para verificar disponibilidad
                }),
            });

            const data = await response.json();
            console.log("Respuesta del backend:", data); // Depuración: Respuesta de la API

            // Actualizar disponibilidad de habitaciones
            const updatedAvailability = roomsData.reduce((acc, room) => {
                acc[room.id] = !data.unavailableRooms?.includes(room.id); // Disponible si no está en unavailableRooms
                return acc;
            }, {});
            console.log("Disponibilidad actualizada:", updatedAvailability); // Depuración: Disponibilidad actualizada

            setAvailability(updatedAvailability);
        } catch (error) {
            console.error("Error al verificar disponibilidad:", error);
        }
    };

    // Llamar a fetchAvailability cada vez que cambia la fecha seleccionada
    useEffect(() => {
        if (selectedDate) {
            fetchAvailability(selectedDate);
        }
    }, [selectedDate]);

    return (
        <div className="home-container">
            <h1>Bienvenido al Hostal</h1>

            {/* Calendario para seleccionar la fecha */}
            <div className="calendar-container">
                <p>Selecciona una fecha de ingreso:</p>
                <DatePicker
                    selected={selectedDate}
                    onChange={(date) => setSelectedDate(date)} // Actualizar fecha seleccionada
                    locale="es"
                    placeholderText="Selecciona una fecha"
                    minDate={new Date()} // No permitir fechas pasadas
                    dateFormat="dd/MM/yyyy" // Mostrar día/mes/año
                    inline
                />
            </div>

            {/* Lista de habitaciones */}
            <RoomList rooms={roomsData} availability={availability} />
        </div>
    );
};

export default Home;
