import React, { useState } from 'react';
import roomsData from '../data/roomsData'; // Datos de las habitaciones fijas
import DateSelector from '../components/Calendar'; // Componente del calendario
import RoomList from '../components/RoomList'; // Componente para listar habitaciones
import axios from 'axios';
import '../styles/Home.css';


const Home = () => {
    const [rooms, setRooms] = useState(roomsData); // Usar las habitaciones fijas
    const [availability, setAvailability] = useState({}); // Almacena disponibilidad dinámica

    const fetchAvailableRooms = async (dates) => {
        try {
            const [checkIn, checkOut] = dates;
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/reservations/availability`, {
                checkIn,
                checkOut,
            });

            // Convertir la respuesta en un objeto de disponibilidad
            const updatedAvailability = rooms.reduce((acc, room) => {
                acc[room.id] = response.data.includes(room.id);
                return acc;
            }, {});
            setAvailability(updatedAvailability);
        } catch (error) {
            console.error('Error al obtener habitaciones disponibles:', error);
        }
    };

    return (
        <div>
            <h1>Bienvenido al Hostal</h1>
            <DateSelector onDatesSelected={fetchAvailableRooms} />
            <RoomList rooms={rooms} availability={availability} />
        </div>
    );
};

export default Home;
