import React from "react";
import { PayPalButtons, usePayPalScriptReducer } from "@paypal/react-paypal-js";

export default function PaypalButton({ amount, onPaymentSuccess, onPaymentError, onPaymentCancel }) {
  const [{ isPending }] = usePayPalScriptReducer();

  const createOrder = (data, actions) => {
    return actions.order.create({
      purchase_units: [
        {
          amount: {
            value: amount.toString(), // Usa el monto dinámico pasado como prop
          },
        },
      ],
    });
  };

  const onApprove = (data, actions) => {
    return actions.order.capture().then((details) => {
      onPaymentSuccess(details); // Llama a la función proporcionada para manejar el éxito
    }).catch((error) => {
      onPaymentError(error); // Llama a la función proporcionada para manejar el error
    });
  };

  const onCancel = () => {
    onPaymentCancel(); // Llama a la función proporcionada para manejar la cancelación
  };

  return (
    <div>
      {isPending ? (
        <div>Cargando...</div>
      ) : (
        <PayPalButtons
          createOrder={createOrder}
          onApprove={onApprove}
          onCancel={onCancel}
          style={{ layout: "vertical" }}
        />
      )}
    </div>
  );
}
