import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Importar useNavigate
import RoomList from '../components/RoomList';
import roomsData from '../data/roomsData';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';
import '../styles/Home.css';

registerLocale("es", es);

const Home = () => {
    const navigate = useNavigate(); // Hook para redirigir a otras rutas

    // Configurar fechas iniciales
    const today = new Date(); // Fecha actual
    const tomorrow = new Date(today.getTime() + 24 * 60 * 60 * 1000); // Día siguiente

    const [checkIn, setCheckIn] = useState(today); // Fecha de ingreso inicial
    const [checkOut, setCheckOut] = useState(tomorrow); // Fecha de salida inicial
    const [availability, setAvailability] = useState({}); // Disponibilidad de habitaciones

    // Ordenar las habitaciones por ID
    const sortedRoomsData = [...roomsData].sort((a, b) => a.id - b.id);

    // Función para verificar disponibilidad en el backend
    const fetchAvailability = async () => {
        if (!checkIn || !checkOut) return; // No hacer nada si faltan fechas
        const formattedCheckIn = checkIn.toISOString().split("T")[0];
        const formattedCheckOut = checkOut.toISOString().split("T")[0];

        try {
            const response = await fetch("http://localhost:5000/api/reservations/check-availability", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    checkIn: formattedCheckIn,
                    checkOut: formattedCheckOut,
                }),
            });

            const data = await response.json();

            // Actualizar la disponibilidad de las habitaciones
            const updatedAvailability = sortedRoomsData.reduce((acc, room) => {
                acc[room.id] = !data.unavailableRooms?.includes(room.id);
                return acc;
            }, {});

            setAvailability(updatedAvailability);
        } catch (error) {
            console.error("Error al verificar disponibilidad:", error);
        }
    };

    // Llamar a la función de disponibilidad cuando cambien las fechas
    useEffect(() => {
        if (checkIn && checkOut) {
            fetchAvailability();
        }
    }, [checkIn, checkOut]);

    // Manejar la selección de fecha de ingreso
    const handleCheckInChange = (date) => {
        setCheckIn(date);

        // Ajustar automáticamente la fecha de salida
        const nextDay = new Date(date.getTime() + 24 * 60 * 60 * 1000); // Sumar un día
        setCheckOut(nextDay); // Ajustar la fecha de salida automáticamente
    };

    // Manejar la selección de fecha de salida
    const handleCheckOutChange = (date) => {
        if (date > checkIn) {
            setCheckOut(date); // Solo permitir fechas posteriores a la fecha de ingreso
        }
    };

    return (
        <div className="home-container">
            <h1 className="home-title">Bienvenido al Hostal</h1>

            {/* Contenedor principal */}
            <div className="calendar-and-button-container">
                {/* Calendario de Ingreso */}
                <div className="calendar-container">
                    <label>Selecciona tu fecha de ingreso:</label>
                    <div className="datepicker-wrapper">
                        <DatePicker
                            selected={checkIn}
                            onChange={handleCheckInChange}
                            locale="es"
                            placeholderText="Fecha de ingreso"
                            minDate={new Date()}
                            dateFormat="dd/MM/yyyy"
                            className="datepicker-input"
                        />
                    </div>
                </div>

                {/* Calendario de Salida */}
                <div className="calendar-container">
                    <label>Selecciona tu fecha de salida:</label>
                    <div className="datepicker-wrapper">
                        <DatePicker
                            selected={checkOut}
                            onChange={handleCheckOutChange}
                            locale="es"
                            placeholderText="Fecha de salida"
                            minDate={new Date(checkIn.getTime() + 24 * 60 * 60 * 1000)} // Un día después de la fecha de ingreso
                            dateFormat="dd/MM/yyyy"
                            className="datepicker-input"
                        />
                    </div>
                </div>

                {/* Botón Administrador */}
                <div className="admin-button-container">
                    <button
                        onClick={() => navigate('/admin')}
                        className="admin-button"
                    >
                        Administrador
                    </button>
                </div>
            </div>

            {/* Lista de habitaciones */}
            <RoomList rooms={sortedRoomsData} availability={availability} />
        </div>
    );
};

export default Home;
