import React, { useContext } from 'react';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import es from 'date-fns/locale/es';
import { ReservationContext } from '../ReservationContext';

registerLocale("es", es);

const RoomList = ({ rooms }) => {
    const { availability, selectedDate, setSelectedDate } = useContext(ReservationContext);

    return (
        <div className="room-list-container">
            <div className="calendar-container">
                <p>Selecciona una fecha para ver disponibilidad:</p>
                <DatePicker
                    selected={selectedDate}
                    onChange={(date) => setSelectedDate(date)}
                    locale="es"
                    placeholderText="Selecciona una fecha"
                    inline
                />
            </div>

            <div className="room-list">
                {rooms.map((room) => (
                    <div key={room.id} className="room-card">
                        <img src={room.image} alt={room.type} className="room-image" />
                        <p className="room-id">ID: <strong>{room.id}</strong></p>
                        <h2>{room.type}</h2>
                        <p>
                            Precio:{" "}
                            <strong>
                                {room.priceForTwo
                                    ? `${room.priceForTwo} CLP`
                                    : `${room.priceForOne} CLP`}
                            </strong>
                        </p>
                        <p className="room-availability">
                            Disponibilidad:{" "}
                            <span className={availability[room.id] ? "available" : "not-available"}>
                                {availability[room.id] ? "Dispoble" : "No disponible"}
                            </span>
                        </p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default RoomList;
