// src/pages/Home.js

import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';   // ← añadimos useNavigate
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import roomsData from '../data/roomsData';
import '../styles/Home.css';
import Footer from '../components/Footer';
import backgroundImage from '../images/background2.jpg';

const Home = () => {
    const today = new Date();
    const exchangeRate = 900;
    const navigate = useNavigate();                       // ← declaramos navigate

    const [checkIn, setCheckIn] = useState(today);
    const [availability, setAvailability] = useState({});
    const [loading, setLoading] = useState(false);
    const [calendarOpen, setCalendarOpen] = useState(false);
    const calendarRef = useRef(null);
    const [selectedOptions, setSelectedOptions] = useState(
        roomsData.reduce((acc, room) => ({ ...acc, [room.id]: 1 }), {})
    );

    const [expandedGroups, setExpandedGroups] = useState({});

    const groupedRooms = roomsData.reduce((acc, room) => {
        const type = room.type;
        if (!acc[type]) { acc[type] = []; }
        acc[type].push(room);
        return acc;
    }, {});

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (calendarRef.current && !calendarRef.current.contains(event.target)) {
                setCalendarOpen(false);
            }
        };
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    useEffect(() => {
        const calculateAvailability = async () => {
            setLoading(true);
            const updatedAvailability = {};
            const selectedDate = new Date(
                checkIn.getTime() - (checkIn.getTimezoneOffset() * 60000)
            )
                .toISOString()
                .split("T")[0];

            try {
                const response = await fetch("https://three-2cs3.onrender.com/api/reservations");
                const allReservations = await response.json();
                for (const room of roomsData) {
                    let isRoomAvailable = true;
                    for (const res of allReservations) {
                        if (res.roomId === room.id) {
                            const resCheckIn = res.checkIn.split('T')[0];
                            const resCheckOut = res.checkOut.split('T')[0];
                            if (selectedDate >= resCheckIn && selectedDate < resCheckOut) {
                                isRoomAvailable = false;
                                break;
                            }
                        }
                    }
                    updatedAvailability[room.id] = isRoomAvailable;
                }
                setAvailability(updatedAvailability);
            } catch (error) {
                console.error("Error al verificar disponibilidad:", error);
            } finally {
                setLoading(false);
            }
        };
        calculateAvailability();
    }, [checkIn]);

    const handleSelectChange = (id, value) => {
        setSelectedOptions((prev) => ({ ...prev, [id]: value }));
    };

    const convertToUSD = (amountInCLP) => {
        return (amountInCLP / exchangeRate).toFixed(2);
    };

    const toggleGroup = (type) => {
        setExpandedGroups(prevExpanded => ({
            ...prevExpanded,
            [type]: !prevExpanded[type]
        }));
    };

    return (
        <div className="home-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <video autoPlay loop muted playsInline className="video-background">
                <source src="/videos/background.mp4" type="video/mp4" />
                Your browser does not support the video tag.
            </video>

            <h1 className="home-title">Bienvenido al Hostal Jallalla</h1>

            <div className="calendar-container">
                <span className="calendar-message">Seleccione una fecha para ver disponibilidad</span>
                <button
                    onClick={() => setCalendarOpen(!calendarOpen)}
                    className="calendar-toggle-btn"
                >
                    {checkIn.toLocaleDateString("es-ES", {
                        day: "2-digit",
                        month: "2-digit",
                        year: "numeric"
                    })}
                </button>
                {calendarOpen && (
                    <div className="calendar-dropdown" ref={calendarRef}>
                        <Calendar
                            onChange={(date) => {
                                setCheckIn(date);
                                setCalendarOpen(false);
                            }}
                            value={checkIn}
                            minDate={today}
                        />
                    </div>
                )}
            </div>

            {loading ? (
                <p className="loading-message">Cargando disponibilidad...</p>
            ) : (
                <div className="room-list">
                    {Object.keys(groupedRooms).map((type) => {
                        const roomsInGroup = groupedRooms[type];

                        // Ordenamos disponibles primero
                        const sortedRooms = [...roomsInGroup].sort((a, b) => {
                            const availabilityA = !!availability[a.id];
                            const availabilityB = !!availability[b.id];
                            if (availabilityA === availabilityB) {
                                return a.id - b.id; // orden estable por id
                            }
                            return availabilityB - availabilityA;
                        });

                        const isExpanded = !!expandedGroups[type];
                        const visibleRooms = isExpanded
                            ? sortedRooms
                            : sortedRooms.slice(0, 1);

                        return (
                            <React.Fragment key={type}>
                                <h2 className="room-group-title">{type}</h2>
                                <div className="room-group">
                                    {visibleRooms.map((room) => (
                                        <div
                                            key={room.id}
                                            className="room-card"
                                            onClick={() =>
                                                availability[room.id] &&
                                                navigate(`/room/${room.id}`)
                                            }
                                            style={{
                                                cursor: availability[room.id]
                                                    ? 'pointer'
                                                    : 'not-allowed'
                                            }}
                                        >
                                            <img
                                                src={room.image}
                                                alt={room.type}
                                                className="room-image"
                                            />
                                            <p className="room-id">
                                                ID: <strong>{room.id}</strong>
                                            </p>
                                            <p>
                                                Precio por noche:{' '}
                                                <strong>
                                                    {selectedOptions[room.id] === 2 && room.priceForTwo
                                                        ? `${room.priceForTwo} CLP ($${convertToUSD(
                                                              room.priceForTwo
                                                          )} USD)`
                                                        : `${room.priceForOne} CLP ($${convertToUSD(
                                                              room.priceForOne
                                                          )} USD)`}
                                                </strong>
                                            </p>
                                            <label>
                                                Personas:{' '}
                                                <select
                                                    value={selectedOptions[room.id] || 1}
                                                    onChange={(e) =>
                                                        handleSelectChange(
                                                            room.id,
                                                            parseInt(e.target.value)
                                                        )
                                                    }
                                                    onClick={(e) => e.stopPropagation()}  // ← evitamos la burbuja de click
                                                >
                                                    <option value={1}>1 Persona</option>
                                                    {room.priceForTwo && (
                                                        <option value={2}>2 Personas</option>
                                                    )}
                                                </select>
                                            </label>
                                            <p className="room-availability">
                                                <span
                                                    className={
                                                        availability[room.id]
                                                            ? 'available'
                                                            : 'not-available'
                                                    }
                                                >
                                                    {availability[room.id]
                                                        ? 'Disponible'
                                                        : 'No disponible'}
                                                </span>
                                            </p>
                                            <Link
                                                to={`/room/${room.id}`}
                                                className={`details-btn ${
                                                    availability[room.id] ? '' : 'disabled'
                                                }`}
                                                style={{
                                                    pointerEvents: availability[room.id]
                                                        ? 'auto'
                                                        : 'none',
                                                    backgroundColor: availability[room.id]
                                                        ? '#007bff'
                                                        : '#ccc',
                                                    cursor: availability[room.id]
                                                        ? 'pointer'
                                                        : 'not-allowed'
                                                }}
                                                onClick={(e) => e.stopPropagation()}  // opcional: evita doble navegación
                                            >
                                                {availability[room.id]
                                                    ? 'Ver Detalles'
                                                    : 'No disponible'}
                                            </Link>
                                        </div>
                                    ))}
                                </div>

                                {roomsInGroup.length > 1 && (
                                    <button
                                        onClick={() => toggleGroup(type)}
                                        className="show-more-btn"
                                    >
                                        {isExpanded
                                            ? 'Ver menos'
                                            : `Ver ${roomsInGroup.length - 1} más...`}
                                        <span
                                            className={`arrow ${
                                                isExpanded ? 'up' : 'down'
                                            }`}
                                        ></span>
                                    </button>
                                )}
                            </React.Fragment>
                        );
                    })}
                </div>
            )}

            <Footer />
        </div>
    );
};

export default Home;
