import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Header.css';

const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    return (
        <header className="header-container">
            <nav className="nav-container">
                <div className="menu-icon" onClick={toggleMenu}>
                    &#9776; {/* Ícono de hamburguesa */}
                </div>
                <ul className={`nav-links ${menuOpen ? 'active' : ''}`}>
                    <li><Link to="/" className="nav-link" onClick={toggleMenu}>Inicio</Link></li>
                    <li><Link to="/nosotros" className="nav-link" onClick={toggleMenu}>Nosotros</Link></li>
                    <li><Link to="/galeria" className="nav-link" onClick={toggleMenu}>Galería</Link></li>
                </ul>
            </nav>
        </header>
    );
};

export default Header;
