import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import "jspdf-autotable";
import "./AdminPanel.css";

const AdminPanel = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const roomPrices = {
        1: { priceForOne: 17000, priceForTwo: 25000 },
        2: { priceForOne: 20000, priceForTwo: 30000 },
        3: { priceForOne: 25000, priceForTwo: 40000 },
    };

    const handleLogin = (e) => {
        e.preventDefault();
        if (username === "admin" && password === "jallalla1478") {
            setIsLoggedIn(true);
        } else {
            alert("Usuario o contraseña incorrectos.");
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        setPassword('');
        setReservations([]);
    };

    const fetchReservations = async () => {
        setLoading(true);
        try {
            const response = await axios.get("https://hostaljallalla2.onrender.com/api/reservations");
            const processedReservations = response.data.map((reservation) => {
                const roomPrice = roomPrices[reservation.roomId] || { priceForOne: 0, priceForTwo: 0 };
                const pricePerNight = reservation.numberOfGuests === 1
                    ? roomPrice.priceForOne
                    : roomPrice.priceForTwo;

                const nights = Math.ceil(
                    (new Date(reservation.checkOut) - new Date(reservation.checkIn)) / (1000 * 60 * 60 * 24)
                );

                return {
                    ...reservation,
                    nights,
                    totalCost: nights * pricePerNight,
                };
            });
            setReservations(processedReservations);
        } catch (error) {
            console.error("Error al obtener reservas:", error);
        } finally {
            setLoading(false);
        }
    };

    const deleteReservation = async (id) => {
        if (!window.confirm("¿Estás seguro de eliminar esta reserva?")) return;
        try {
            await axios.delete(`https://hostaljallalla2.onrender.com/api/reservations/${id}`);
            fetchReservations();
        } catch (error) {
            console.error("Error al eliminar reserva:", error);
        }
    };

    const downloadPDF = () => {
        const doc = new jsPDF();
        doc.setFontSize(16);
        doc.text("Reporte de Reservas", 10, 10);
        doc.setFontSize(12);
        doc.text(`Fecha: ${new Date().toLocaleDateString()}`, 10, 20);

        // Configuración de columnas
        const tableColumn = [
            { header: "Cliente", dataKey: "clientName" },
            { header: "Teléfono", dataKey: "phone" },
            { header: "RUT/Pasaporte", dataKey: "rutOrPassport" },
            { header: "Nacionalidad", dataKey: "nationality" },
            { header: "Dirección", dataKey: "address" },
            { header: "Habitación", dataKey: "roomId" },
            { header: "Check-In", dataKey: "checkIn" },
            { header: "Check-Out", dataKey: "checkOut" },
            { header: "Noches", dataKey: "nights" },
            { header: "Precio Total", dataKey: "totalCost" },
        ];

        // Filas de la tabla
        const tableRows = reservations.map((res) => ({
            clientName: res.clientName,
            phone: res.phone || "No disponible",
            rutOrPassport: res.rutOrPassport,
            nationality: res.nationality,
            address: res.address,
            roomId: res.roomId,
            checkIn: new Date(res.checkIn).toLocaleDateString(),
            checkOut: new Date(res.checkOut).toLocaleDateString(),
            nights: res.nights,
            totalCost: `${res.totalCost?.toLocaleString()} CLP`,
        }));

        // Generar la tabla con ajustes
        doc.autoTable({
            head: [tableColumn.map(col => col.header)], // Encabezados
            body: tableRows.map(row => Object.values(row)), // Filas
            startY: 30,
            styles: { fontSize: 10 }, // Tamaño de fuente
            columnStyles: {
                9: { halign: "right", cellWidth: 30 }, // Ajustar la columna Precio Total
                7: { cellWidth: 35 }, // Ajustar Check-In
                8: { cellWidth: 35 }, // Ajustar Check-Out
            },
            margin: { left: 10, right: 10 },
        });

        // Descargar el PDF
        doc.save("reporte_reservas.pdf");
    };

    useEffect(() => {
        if (isLoggedIn) fetchReservations();
    }, [isLoggedIn]);

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            {!isLoggedIn ? (
                <form onSubmit={handleLogin}>
                    <h2>Iniciar Sesión</h2>
                    <input
                        type="text"
                        placeholder="Usuario"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        style={{ padding: "10px", marginBottom: "10px", width: "200px" }}
                        required
                    />
                    <br />
                    <input
                        type="password"
                        placeholder="Contraseña"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        style={{ padding: "10px", marginBottom: "10px", width: "200px" }}
                        required
                    />
                    <br />
                    <button type="submit" style={{ padding: "10px 20px", cursor: "pointer" }}>
                        Ingresar
                    </button>
                </form>
            ) : (
                <div>
                    <h2>Panel de Administración</h2>
                    <button onClick={handleLogout} className="back-btn">Cerrar Sesión</button>
                    <button onClick={() => navigate("/")} className="back-btn">Volver a Inicio</button>
                    <button onClick={downloadPDF} className="back-btn">Descargar PDF</button>
                    {loading ? (
                        <p>Cargando reservas...</p>
                    ) : reservations.length > 0 ? (
                        <table>
                            <thead>
                                <tr>
                                    <th>Cliente</th>
                                    <th>Teléfono</th>
                                    <th>RUT/Pasaporte</th>
                                    <th>Nacionalidad</th>
                                    <th>Dirección</th>
                                    <th>Habitación</th>
                                    <th>Check-In</th>
                                    <th>Check-Out</th>
                                    <th>Noches</th>
                                    <th>Precio Total</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {reservations.map((res) => (
                                    <tr key={res._id}>
                                        <td>{res.clientName}</td>
                                        <td>{res.phone || "No disponible"}</td>
                                        <td>{res.rutOrPassport}</td>
                                        <td>{res.nationality}</td>
                                        <td>{res.address}</td>
                                        <td>{res.roomId}</td>
                                        <td>{new Date(res.checkIn).toLocaleDateString()}</td>
                                        <td>{new Date(res.checkOut).toLocaleDateString()}</td>
                                        <td>{res.nights}</td>
                                        <td>{res.totalCost?.toLocaleString()} CLP</td>
                                        <td>
                                            <button className="delete-btn" onClick={() => deleteReservation(res._id)}>
                                                Eliminar
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>No hay reservas disponibles.</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default AdminPanel;
