import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './pages/Home';
import RoomDetails from './pages/RoomDetails';
import ReservationSuccess from './pages/ReservationSuccess';
import AdminPanel from './pages/AdminPanel';
import Nosotros from './pages/Nosotros'; // Página Nosotros
import Galeria from './pages/Galeria';   // Página Galería
import Header from './components/Header'; // Encabezado
import Footer from './components/Footer'; // Pie de página

const App = () => {
    return (
        <Router>
            {/* Encabezado */}
            <Header />

            {/* Rutas */}
            <Routes>
                <Route path="/" element={<Home />} /> {/* Página de inicio */}
                <Route path="/room/:id" element={<RoomDetails />} /> {/* Detalles de la habitación */}
                <Route path="/success" element={<ReservationSuccess />} /> {/* Página de éxito */}
                <Route path="/admin" element={<AdminPanel />} /> {/* Página de administrador */}
                <Route path="/nosotros" element={<Nosotros />} /> {/* Página Nosotros */}
                <Route path="/galeria" element={<Galeria />} /> {/* Página Galería */}
            </Routes>

            {/* Pie de página */}
            <Footer />
        </Router>
    );
};

export default App;
