import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AdminPanel.css";

const AdminPanel = () => {
    const [reservations, setReservations] = useState([]);
    const [filters, setFilters] = useState({ startDate: "", endDate: "", roomId: "" });
    const [loading, setLoading] = useState(false);

    const roomPrices = {
        1: { priceForOne: 17000, priceForTwo: 25000 },
        2: { priceForOne: 20000, priceForTwo: 30000 },
        3: { priceForOne: 25000, priceForTwo: 40000 },
        // Agrega más habitaciones según tus datos
    };

    const fetchReservations = async () => {
        setLoading(true);
        try {
            const response = await axios.get("/api/reservations", { params: filters });
            const processedReservations = response.data.map((reservation) => {
                const roomPrice = roomPrices[reservation.roomId] || { priceForOne: 0, priceForTwo: 0 };
                const pricePerNight = reservation.numberOfGuests === 1
                    ? roomPrice.priceForOne
                    : roomPrice.priceForTwo;

                const nights = Math.ceil(
                    (new Date(reservation.checkOut) - new Date(reservation.checkIn)) / (1000 * 60 * 60 * 24)
                );

                return {
                    ...reservation,
                    nights,
                    totalCost: nights * pricePerNight,
                };
            });
            setReservations(processedReservations);
        } catch (error) {
            console.error("Error al obtener reservas:", error);
        } finally {
            setLoading(false);
        }
    };

    const deleteReservation = async (id) => {
        // Confirmar la eliminación con el usuario
        if (!window.confirm("¿Estás seguro de eliminar esta reserva?")) return;
    
        try {
            // Petición DELETE al servidor
            await axios.delete(`/api/reservations/${id}`);
            alert("Reserva eliminada correctamente");
    
            // Actualizar la lista de reservas después de eliminar
            fetchReservations();
        } catch (error) {
            console.error("Error al eliminar reserva:", error);
            alert("Hubo un problema al eliminar la reserva.");
        }
    };
    

    const handleFilterChange = (e) => {
        setFilters({ ...filters, [e.target.name]: e.target.value });
    };

    const handleSearch = () => {
        fetchReservations();
    };

    useEffect(() => {
        fetchReservations();
    }, []);

    return (
        <div>
            <h1>Panel de Administración</h1>
            <div>
                <label>Fecha de Inicio:</label>
                <input type="date" name="startDate" value={filters.startDate} onChange={handleFilterChange} />
                <label>Fecha de Fin:</label>
                <input type="date" name="endDate" value={filters.endDate} onChange={handleFilterChange} />
                <label>ID de Habitación:</label>
                <input type="text" name="roomId" value={filters.roomId} onChange={handleFilterChange} />
                <button onClick={handleSearch}>Buscar</button>
            </div>
            {loading ? (
                <p>Cargando reservas...</p>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Cliente</th>
                            <th>Correo</th>
                            <th>Habitación</th>
                            <th>Check-In</th>
                            <th>Check-Out</th>
                            <th>Noches</th>
                            <th>Precio Total</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {reservations.length > 0 ? (
                            reservations.map((res) => (
                                <tr key={res._id}>
                                    <td>{res._id}</td>
                                    <td>{res.clientName}</td>
                                    <td>{res.clientEmail}</td>
                                    <td>{res.roomId}</td>
                                    <td>{new Date(res.checkIn).toLocaleDateString()}</td>
                                    <td>{new Date(res.checkOut).toLocaleDateString()}</td>
                                    <td>{res.nights}</td>
                                    <td>{res.totalCost?.toLocaleString()} CLP</td>
                                    <td>
                                        <button onClick={() => deleteReservation(res._id)}>Eliminar</button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="9">No hay reservas disponibles</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default AdminPanel;
