import React, { useState, useRef, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Header.css'; // Asegúrate de que la ruta sea correcta
import headerImage from '../images/header-background.jpg'; // Asegúrate de que la ruta sea correcta

const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);
    const navigate = useNavigate();
    const menuRef = useRef(null); // Ref para el menú

    // Cierra el menú si se hace clic fuera de él
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setMenuOpen(false);
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [menuRef]);

    return (
        <header
            className="header-container"
            style={{ backgroundImage: `url(${headerImage})` }}
        >
            {/* Logo a la izquierda */}
            <Link to="/" className="logo-container">
                <img src="/images/logo.png" alt="Logo del Hostal" className="logo" />
                <span className="logo-text">Hostal Jallalla</span>
            </Link>

            {/* Contenedor para toda la navegación derecha */}
            <div className="navigation-area">
                {/* Enlaces que siempre se ven en escritorio */}
                <nav className="main-nav">
                    <ul className="nav-links">
                        <li><Link to="/" className="nav-button">Inicio</Link></li>
                        <li><Link to="/nosotros" className="nav-button">Nosotros</Link></li>
                        <li><Link to="/galeria" className="nav-button">Galería</Link></li>
                    </ul>
                </nav>

                {/* Botón de admin para escritorio */}
                <div className="admin-button-desktop">
                    <button className="admin-button" onClick={() => navigate('/admin')}>
                        Administrador
                    </button>
                </div>

                {/* Menú hamburguesa y desplegable para móvil */}
                <div className="mobile-menu" ref={menuRef}>
                    <button className="menu-toggle" onClick={() => setMenuOpen(!menuOpen)}>
                        <span className="hamburger-line"></span>
                        <span className="hamburger-line"></span>
                        <span className="hamburger-line"></span>
                    </button>

                    {menuOpen && (
                        <div className="dropdown-menu">
                            <button className="admin-button" onClick={() => {
                                navigate('/admin');
                                setMenuOpen(false);
                            }}>
                                Administrador
                            </button>
                        </div>
                    )}
                </div>
            </div>
        </header>
    );
};

export default Header;
