import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./AdminPanel.css";

const AdminPanel = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false); // Estado de login
    const [password, setPassword] = useState(""); // Contraseña
    const [reservations, setReservations] = useState([]); // Estado de reservas
    const [loading, setLoading] = useState(false); // Estado de carga
    const navigate = useNavigate();

    // Función para manejar el login
    const handleLogin = (e) => {
        e.preventDefault();
        if (password === "admin123") { // Contraseña fija para el login (puedes cambiarla)
            setIsLoggedIn(true);
        } else {
            alert("Contraseña incorrecta.");
        }
    };

    // Función para cerrar sesión
    const handleLogout = () => {
        setIsLoggedIn(false);
        setPassword("");
        setReservations([]);
    };

    // Obtener reservas desde la base de datos
    const fetchReservations = async () => {
        setLoading(true);
        try {
            const response = await axios.get("http://localhost:5000/api/reservations"); // Ruta del backend
            setReservations(response.data); // Guardar reservas en el estado
        } catch (error) {
            console.error("Error al obtener reservas:", error);
        } finally {
            setLoading(false);
        }
    };

    // Eliminar una reserva
    const deleteReservation = async (id) => {
        if (!window.confirm("¿Estás seguro de eliminar esta reserva?")) return;
        try {
            await axios.delete(`http://localhost:5000/api/reservations/${id}`);
            alert("Reserva eliminada correctamente.");
            fetchReservations(); // Actualizar la lista
        } catch (error) {
            console.error("Error al eliminar la reserva:", error);
        }
    };

    // Cargar reservas cuando el administrador inicie sesión
    useEffect(() => {
        if (isLoggedIn) {
            fetchReservations();
        }
    }, [isLoggedIn]);

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            {/* Login */}
            {!isLoggedIn ? (
                <form onSubmit={handleLogin}>
                    <h2>Iniciar Sesión</h2>
                    <input
                        type="password"
                        placeholder="Contraseña"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        style={{ padding: "10px", marginBottom: "10px", width: "200px" }}
                        required
                    />
                    <br />
                    <button type="submit" style={{ padding: "10px 20px", cursor: "pointer" }}>
                        Ingresar
                    </button>
                </form>
            ) : (
                // Panel de reservas
                <div>
                    <h2>Panel de Administración</h2>
                    <button
                        onClick={handleLogout}
                        style={{
                            marginBottom: "20px",
                            padding: "10px 20px",
                            cursor: "pointer",
                            backgroundColor: "red",
                            color: "white",
                            border: "none",
                            borderRadius: "4px",
                        }}
                    >
                        Cerrar Sesión
                    </button>
                    <button
                        onClick={() => navigate("/")}
                        style={{
                            marginLeft: "10px",
                            padding: "10px 20px",
                            cursor: "pointer",
                            backgroundColor: "#007bff",
                            color: "white",
                            border: "none",
                            borderRadius: "4px",
                        }}
                    >
                        Volver a Inicio
                    </button>
                    {loading ? (
                        <p>Cargando reservas...</p>
                    ) : reservations.length > 0 ? (
                        <table style={{ margin: "0 auto", borderCollapse: "collapse", width: "80%" }}>
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Cliente</th>
                                    <th>Habitación</th>
                                    <th>Check-In</th>
                                    <th>Check-Out</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {reservations.map((res) => (
                                    <tr key={res._id}>
                                        <td>{res._id}</td>
                                        <td>{res.clientName}</td>
                                        <td>{res.roomId}</td>
                                        <td>{new Date(res.checkIn).toLocaleDateString()}</td>
                                        <td>{new Date(res.checkOut).toLocaleDateString()}</td>
                                        <td>
                                            <button
                                                onClick={() => deleteReservation(res._id)}
                                                style={{
                                                    backgroundColor: "red",
                                                    color: "white",
                                                    border: "none",
                                                    padding: "5px 10px",
                                                    cursor: "pointer",
                                                }}
                                            >
                                                Eliminar
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>No hay reservas registradas.</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default AdminPanel;
