import React, { useState } from 'react';
import ReservationForm from './ReservationForm';

const RoomCard = ({ room }) => {
    const [showForm, setShowForm] = useState(false);

    return (
        <div className="room-card">
            <img src={room.image} alt={`Habitación ${room.id}`} className="room-image" />
            <h3>{room.type}</h3>
            <p>ID: {room.id}</p>
            <p>Precio para 1 persona: {room.priceForOne} CLP</p>
            {room.priceForTwo && <p>Precio para 2 personas: {room.priceForTwo} CLP</p>}
            <p>
                Disponibilidad:{" "}
                <span style={{ color: room.available ? "green" : "red" }}>
                    {room.available ? "Disponible" : "No disponible"}
                </span>
            </p>
            {room.available && (
                <button onClick={() => setShowForm(true)}>Reservar</button>
            )}
            {showForm && (
                <ReservationForm room={room} onClose={() => setShowForm(false)} />
            )}
        </div>
    );
};

export default RoomCard;
