import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import rooms from '../data/rooms';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import '../styles/Home.css'; // Usar los mismos estilos que en Home.js

const RoomDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const room = rooms.find((room) => room.id === parseInt(id));

    const [selectedDate, setSelectedDate] = useState(new Date()); // Fecha de ingreso
    const [checkOut, setCheckOut] = useState(null); // Fecha de salida
    const [isAvailable, setIsAvailable] = useState(false); // Disponibilidad de la habitación
    const [calendarOpen, setCalendarOpen] = useState(false); // Estado para desplegar/ocultar calendario
    const [error, setError] = useState(''); // Error si fechas coinciden

    // Verificar disponibilidad conectando con el backend
    useEffect(() => {
        const checkAvailability = async () => {
            if (selectedDate && checkOut) {
                if (selectedDate.toISOString().split("T")[0] === checkOut.toISOString().split("T")[0]) {
                    setError("La fecha de ingreso no puede ser igual a la de salida.");
                    setIsAvailable(false);
                    return;
                }

                try {
                    const response = await fetch("https://hostaljallalla2.onrender.com/api/reservations/check-availability", {
                        method: "POST",
                        headers: { "Content-Type": "application/json" },
                        body: JSON.stringify({
                            checkIn: selectedDate.toISOString().split("T")[0],
                            checkOut: checkOut.toISOString().split("T")[0],
                            roomId: room.id,
                        }),
                    });

                    const data = await response.json();
                    setError(""); // Limpiar errores si las fechas son válidas
                    setIsAvailable(!data.unavailableRooms?.includes(room.id));
                } catch (error) {
                    console.error("Error al verificar disponibilidad:", error);
                    setIsAvailable(false);
                }
            }
        };

        checkAvailability();
    }, [selectedDate, checkOut, room.id]);

    // Manejar la selección de fecha de ingreso
    const handleCheckInChange = (date) => {
        setSelectedDate(date);

        if (!checkOut || date >= checkOut) {
            const nextDay = new Date(date.getTime() + 24 * 60 * 60 * 1000);
            setCheckOut(nextDay);
        }
    };

    // Manejar la selección de fecha de salida
    const handleCheckOutChange = (date) => {
        setCheckOut(date);
    };

    return (
        <div style={{ padding: "30px", textAlign: "center" }}>
            <h1
                style={{
                    display: 'inline-block',
                    padding: '10px 20px',
                    fontSize: '16px',
                    fontWeight: 'bold',
                    color: 'white',
                    backgroundColor: '#007bff',
                    borderRadius: '8px',
                    textAlign: 'center',
                    cursor: 'default',
                }}
            >
                {room.type}
            </h1>
            <p><strong>ID:</strong> {room.id}</p>
            <img
                src={room.image}
                alt={room.type}
                style={{
                    width: "80%",
                    maxWidth: "220px",
                    borderRadius: "8px",
                    marginBottom: "20px",
                }}
            />

            <p>
                Precio por noche: {" "}
                <strong>
                    {room.priceForOne} CLP
                </strong>
            </p>

            {/* Calendario Fecha de Ingreso */}
            <div className="calendar-container">
                <label>Fecha de ingreso:</label>
                <button
                    onClick={() => setCalendarOpen(!calendarOpen)}
                    className="calendar-toggle-btn"
                >
                    {selectedDate.toLocaleDateString("es-ES", { day: "2-digit", month: "2-digit", year: "numeric" })}
                </button>

                {calendarOpen && (
                    <div className="calendar-dropdown">
                        <Calendar
                            onChange={(date) => {
                                handleCheckInChange(date);
                                setCalendarOpen(false); // Cerrar calendario al seleccionar fecha
                            }}
                            value={selectedDate}
                            minDate={new Date()} // No permitir fechas pasadas
                        />
                    </div>
                )}
            </div>

            {/* Calendario Fecha de Salida */}
            <div className="calendar-container">
                <label>Fecha de salida:</label>
                <Calendar
                    onChange={handleCheckOutChange}
                    value={checkOut}
                    minDate={selectedDate ? new Date(selectedDate.getTime() + 24 * 60 * 60 * 1000) : new Date()} // Mínimo un día después
                />
            </div>

            {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}

            {selectedDate && checkOut && !error && (
                <p
                    style={{
                        fontSize: '18px',
                        fontWeight: 'bold',
                        color: isAvailable ? '#28a745' : '#dc3545',
                        textAlign: 'center',
                    }}
                >
                    {isAvailable ? "¡Disponible!" : "No disponible"}
                </p>
            )}
        </div>
    );
};

export default RoomDetails;
