import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import roomsData from '../data/roomsData';
import '../styles/Home.css';
import Footer from '../components/Footer';
import backgroundImage from '../images/background2.jpg';

const Home = () => {
  const today = new Date();
  const exchangeRate = 900; // Tasa fija: 1 USD = 900 CLP

  const [checkIn, setCheckIn] = useState(today);
  const [availability, setAvailability] = useState({});
  const [loading, setLoading] = useState(false);
  const [calendarOpen, setCalendarOpen] = useState(false);
  const calendarRef = useRef(null);

  const [selectedOptions, setSelectedOptions] = useState(
    roomsData.reduce((acc, room) => ({ ...acc, [room.id]: 1 }), {})
  );

  // <-- INICIA CAMBIO LÓGICO
  // 1. Agrupamos las habitaciones por su tipo (type)
  const groupedRooms = roomsData.reduce((acc, room) => {
    const type = room.type;
    if (!acc[type]) {
      acc[type] = [];
    }
    acc[type].push(room);
    return acc;
  }, {});
  // <-- TERMINA CAMBIO LÓGICO

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (calendarRef.current && !calendarRef.current.contains(event.target)) {
        setCalendarOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  useEffect(() => {
    const calculateAvailability = async () => {
      setLoading(true);
      const updatedAvailability = {};

      try {
        const response = await fetch("https://three-2cs3.onrender.com/api/reservations");
        const result = await response.json();
        const data = Array.isArray(result) ? result : result.reservations || [];

        for (const room of roomsData) { // Usamos roomsData para calcular disponibilidad de todas
          const reservations = data.filter((res) => res.roomId === room.id);
          const selectedDate = new Date(checkIn).getTime();
          let isAvailable = true;

          for (const res of reservations) {
            const resCheckIn = new Date(res.checkIn).getTime();
            const resCheckOut = new Date(res.checkOut).getTime();
            if (selectedDate >= resCheckIn && selectedDate < resCheckOut) {
              isAvailable = false;
              break;
            }
            if (selectedDate === resCheckOut) {
              isAvailable = true;
              break;
            }
          }
          updatedAvailability[room.id] = isAvailable;
        }
        setAvailability(updatedAvailability);
      } catch (error) {
        console.error("Error al verificar disponibilidad:", error);
      } finally {
        setLoading(false);
      }
    };

    calculateAvailability();
  }, [checkIn]);

  const handleSelectChange = (id, value) => {
    setSelectedOptions((prev) => ({ ...prev, [id]: value }));
  };

  const convertToUSD = (amountInCLP) => {
    return (amountInCLP / exchangeRate).toFixed(2);
  };

  return (
    <div className="home-container">
      <video autoPlay loop muted playsInline className="video-background">
        <source src="/videos/background.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <h1 className="home-title">Bienvenido al Hostal Jallalla</h1>

      <div className="calendar-container">
        <span className="calendar-message">Seleccione una fecha para ver disponibilidad</span>
        <button
          onClick={() => setCalendarOpen(!calendarOpen)}
          className="calendar-toggle-btn"
        >
          {checkIn.toLocaleDateString("es-ES", { day: "2-digit", month: "2-digit", year: "numeric" })}
        </button>

        {calendarOpen && (
          <div className="calendar-dropdown" ref={calendarRef}>
            <Calendar
              onChange={(date) => {
                setCheckIn(date);
                setCalendarOpen(false);
              }}
              value={checkIn}
              minDate={today}
            />
          </div>
        )}
      </div>

      {loading ? (
        <p className="loading-message">Cargando disponibilidad...</p>
      ) : (
        <div className="room-list">
          {/* <-- INICIA CAMBIO LÓGICO */}
          {/* 2. Iteramos sobre el objeto de grupos en lugar de la lista plana */}
          {Object.keys(groupedRooms).map((type) => (
            <React.Fragment key={type}>
              <h2 className="room-group-title">{type}</h2>
              <div className="room-group">
                {groupedRooms[type]
                  .sort((a, b) => a.id - b.id) // Opcional: ordenar por ID dentro de cada grupo
                  .map((room) => (
                  <div key={room.id} className="room-card">
                    <img src={room.image} alt={room.type} className="room-image" />
                    <p className="room-id">ID: <strong>{room.id}</strong></p>
                    {/* No repetimos el título `h2` porque ya está afuera del grupo */}
                    
                    <p>
                      Precio por noche: <strong>
                        {selectedOptions[room.id] === 2 && room.priceForTwo
                          ? `${room.priceForTwo} CLP ($${convertToUSD(room.priceForTwo)} USD)`
                          : `${room.priceForOne} CLP ($${convertToUSD(room.priceForOne)} USD)`}
                      </strong>
                    </p>

                    <label>
                      Personas:
                      <select
                        value={selectedOptions[room.id] || 1}
                        onChange={(e) => handleSelectChange(room.id, parseInt(e.target.value))}
                      >
                        <option value={1}>1 Persona</option>
                        {room.priceForTwo && <option value={2}>2 Personas</option>}
                      </select>
                    </label>

                    <p className="room-availability">
                      <span className={availability[room.id] ? "available" : "not-available"}>
                        {availability[room.id] ? "Disponible" : "No disponible"}
                      </span>
                    </p>

                    <Link
                      to={`/room/${room.id}`}
                      className={`details-btn ${availability[room.id] ? "" : "disabled"}`}
                      style={{
                        pointerEvents: availability[room.id] ? "auto" : "none",
                        backgroundColor: availability[room.id] ? "#007bff" : "#ccc",
                        cursor: availability[room.id] ? "pointer" : "not-allowed",
                      }}
                    >
                      {availability[room.id] ? "Ver Detalles" : "No disponible"}
                    </Link>
                  </div>
                ))}
              </div>
            </React.Fragment>
          ))}
          {/* <-- TERMINA CAMBIO LÓGICO */}
        </div>
      )}
    </div>
  );
};

export default Home;
