import React, { useEffect } from 'react';

const PaypalButton = ({ amount }) => {
    useEffect(() => {
        const script = document.createElement('script');
        script.src = "https://www.paypal.com/sdk/js?client-id=AXkwE6XGyg6ipss01eHDHs4rDjgbntWWQxmpdXRds8s3ilz49XYTRlEN7FEEK3DazkaLNYw5DKhH8OLH&currency=USD";
        script.async = true;

        // Manejar la carga del SDK
        script.onload = () => {
            console.log("PayPal SDK cargado correctamente");
            window.paypal.Buttons({
                // Crear la orden de compra
                createOrder: (data, actions) => {
                    if (!amount || isNaN(amount)) {
                        console.error("Monto inválido:", amount);
                        return Promise.reject("Monto inválido"); // Detiene el flujo si el monto no es válido
                    }

                    return actions.order.create({
                        purchase_units: [{
                            amount: {
                                value: amount.toFixed(2) // Asegúrate de que sea un número con dos decimales
                            }
                        }]
                    });
                },
                // Manejar la aprobación del pago
                onApprove: (data, actions) => {
                    return actions.order.capture()
                        .then(details => {
                            alert(`Pago completado por: ${details.payer.name.given_name}`);
                        })
                        .catch(error => {
                            console.error("Error al capturar el pago:", error);
                        });
                },
                // Manejar errores del botón
                onError: (error) => {
                    console.error("Error en PayPal Buttons:", error);
                }
            }).render('#paypal-button-container');
        };

        script.onerror = () => {
            console.error("Error al cargar el SDK de PayPal");
        };

        document.body.appendChild(script);

        return () => {
            // Limpieza del script al desmontar el componente
            document.body.removeChild(script);
        };
    }, [amount]);

    return <div id="paypal-button-container"></div>;
};

export default PaypalButton;
