import React, { useEffect } from 'react';

const PaypalButton = ({ amount, onPaymentSuccess }) => {
    useEffect(() => {
        // Verifica si el SDK ya está cargado para evitar duplicados
        const existingScript = document.querySelector('script[src^="https://www.paypal.com/sdk/js"]');
        if (existingScript) {
            console.log("El SDK de PayPal ya está cargado");
            renderButton();
            return;
        }

        const script = document.createElement('script');
        script.src = "https://www.paypal.com/sdk/js?client-id=AeFebceon3ssDDM7_w5Cx7l9uw2H2q3-oskUUQPbw4FTe8ljtuyK4hW4FsYbkTtmZHwNnNTUwY4XILg-&currency=USD&debug=true";
        script.async = true;

        script.onload = () => {
            console.log("PayPal SDK cargado correctamente");
            renderButton();
        };

        script.onerror = () => {
            console.error("Error al cargar el SDK de PayPal");
        };

        document.body.appendChild(script);

        const renderButton = () => {
            const container = document.getElementById("paypal-button-container");
            if (container) {
                container.innerHTML = ""; // Limpia el contenedor antes de renderizar
            }

            window.paypal.Buttons({
                createOrder: (data, actions) => {
                    const validAmount = amount || 0; // Validar monto
                    if (isNaN(validAmount) || validAmount <= 0) {
                        console.error("Monto inválido:", validAmount);
                        return Promise.reject("Monto inválido");
                    }

                    return actions.order.create({
                        purchase_units: [{
                            amount: {
                                value: validAmount.toFixed(2) // Convertir a 2 decimales
                            }
                        }]
                    });
                },
                onApprove: (data, actions) => {
                    return actions.order.capture()
                        .then(details => {
                            console.log("Pago completado:", details);
                            if (onPaymentSuccess) {
                                onPaymentSuccess(details); // Llama a la función de éxito pasada como prop
                            }
                        })
                        .catch(error => {
                            console.error("Error al capturar el pago:", error);
                        });
                },
                onError: (error) => {
                    console.error("Error en PayPal Buttons:", error);
                }
            }).render('#paypal-button-container');
        };

        return () => {
            const container = document.getElementById("paypal-button-container");
            if (container) {
                container.innerHTML = ""; // Limpia el contenedor
            }
            if (script) {
                document.body.removeChild(script); // Limpia el script del DOM
            }
        };
    }, [amount, onPaymentSuccess]);

    return <div id="paypal-button-container"></div>;
};

export default PaypalButton;
