import React, { createContext, useState } from 'react';

// Crear el contexto
export const ReservationContext = createContext();

// Proveedor del contexto
export const ReservationProvider = ({ children }) => {
    const [selectedDate, setSelectedDate] = useState(null); // Fecha seleccionada
    const [availability, setAvailability] = useState({}); // Disponibilidad de habitaciones

    return (
        <ReservationContext.Provider value={{ selectedDate, setSelectedDate, availability, setAvailability }}>
            {children}
        </ReservationContext.Provider>
    );
};
